css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("maw.ru") {

img{display: none !important;} 

BODY, P, TD, BR, TABLE {
  font-family: Verdana !important;
  font-size: 10pt !important;
  background: hsl(0,17%,60%) !important ; 
  border: 1px !important ; 
  color: hsl(0, 80%, 80%) !important;} 

TABLE {
  border: hsl(0, 40%, 40%) 1px solid !important ;
  text-align: left !important;} 

h1 {font-size: 12pt}

.bigf, .koko {
  font-size: 6pt !important;
  color: hsl(0, 80%, 80%) !important;}

a:link, a:visited, a:active {
  text-decoration: none;
  color: hsl(0, 40%, 40%) !important;
  font-weight: bold !important;}
a:hover {
  text-decoration: none !important;
  font-weight: bold !important;
  color: hsl(0, 100%, 60%) !important;}

table.myt { 
  background-color: hsl(0, 20%, 60%) !important;
  border: 2px solid hsl(0, 50%, 30%)  !important;}
  
input,text,#input,#text,edit,#edit,message,#message,textarea,#textarea{
	background: hsl(0, 30%, 70%) !important;
	color: hsl(0, 70%, 20%) !important;
	border: hsl(0,30%,50%) !important;
	}

}
