css:
/*
 * Animersion skin
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xbtt.animersion.net")

{

body {
background-color: #0d3056 !important;
color: #CCC !important;
font-family: Tahoma !important;
}

a, a:hover {
color: #ec800d !important;
text-decoration: none !important;
font-family: Tahoma !important;
}

a:hover
{
color: #f2a655 !important;
}

input, select {
background: #6b8bae !important;
border: 1px solid #97a9bc !important;
border-bottom: 1px solid #4e6379 !important;
border-right: 1px solid #4e6379 !important;
font-family: Tahoma !important;
}

input[type*="submit"] {
background: #b5c7db !important;
border: 1px solid #97a9bc !important;
border-bottom: 1px solid #4e6379 !important;
border-right: 1px solid #4e6379 !important;
font-family: Tahoma !important;
}

table[height*="140"]{
display: none !important;
}

table.lista {
background: #163a61 !important;
border-color: #173c63 !important;
}

td.header {
background: #31649b !important;
color: #CCC !important;
}

td.block {
background: #567598 !important;
color: #BFBFBF !important;
border: 0 !important;
padding-top: 3px !important;
padding-bottom: 3px !important;
}

td.lista{
background: #19416d !important;
color: #AAA !important;
text-align: center;
}

td.lista[align*="left"]{
text-align: left;
}

td.yellow, td.red, td.green {
background: #19416d !important;
font-weight: bold;
text-align: center;
}

td.green, font[color*="green"] {
color: #37a837 !important;
}

td[height*="20%"], td[width*="20%"], td[align*="center"][colspan*="2"], p.lista {
display: none !important;
}

}
