css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("exmormon.org") {

body
{
color: green;
font-size: 16px;
font-family: times new roman;
background-color: #ddd !important;
margin: 10px 13% 30px 13% !important;
}

a {
color: #006 !important;
border-bottom: dotted 1px #117 !important;

}

.PhorumReadBodyHead {
font-size: 20px;
background: #f1f1f1;
}

.PhorumReadBodyHead a {
border: none !important;
color: #000 !important;
}

.PhorumReadBodyText {
color: black;
font: normal 24px cambria !important;
line-height: 140% !important;
background: #fcfcfc !important;
}

td, th {
font: normal 20px arial !important;
line-height: 140% !important;
padding: 15px !important;
}

.PhorumTableRowAlt {
background-color: #f3f3f3 !important;
}

.PhorumStdBlock {
padding: 10px !important;
margin-top: 20px !important;
}


}
