css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://forums.station.sony.com/eq2/), url-prefix(https://forums.station.sony.com/eq2/) {

/* Alternate stylesheet for default EQ2 forum skin */
/* by Seagoat! */
/* http://eq2.lunaclick.net */

/*ANNOUNCEMENTS*/
	.announcement { color: #f1f1f1 ! important;
		border: 1px solid #000000 ! important; 
		padding: 0px ! important; 
		margin: 3px 0px ! important; }
	.announcementBody { color: #f1f1f1 ! important;
		padding: 0px ! important; }

/* BODY */
	body { background-color: #2c2922 ! important; }

/* INPUTS */
	input, textarea, select { border: 1px solid #7f7949 ! important;
		font: 12px Trebuchet MS, Verdana, sans-serif ! important;
		color: #f1f1f1 ! important;
		background: #31221f ! important;
		text-indent: 0px ! important;}
	input.button { font-size: 11px ! important;
		color: #f1f1f1 ! important;
		font-family: Trebuchet MS, Verdana, sans-serif ! important;
		background-color: #31221f ! important; }
	input.mainoption { font-weight: bold ! important;
		background-color: #31221f ! important; }
	input.liteoption { font-weight: normal ! important;
		background-color: #31221f ! important; }

/* IMAGES */
	img { border: 0px ! important; }
	.skinThumbnail { border: 1px solid #000000 ! important; }

/* FONTS */
	font, th, td, p { font-family: Trebuchet MS, Verdana, sans-serif ! important; }
	.summary { font-style: italic ! important;
		font-size: 13px ! important; }
	.strong { font-weight: bold ! important; }
	.error { font-size: 13px ! important;
		font-weight: bold ! important;
		color: #FF0000 ! important; }
	.offline { color: #FF0000 ! important; }
	.online { color: #00cc00 ! important; }
	cite { font-weight: bold ! important;
		font-style: italic ! important;
		font-size: 13px ! important; }
	pre { font-family: courier, 'courier new', sans-serif ! important;  }
	
	/*h1 { font-weight: bold ! important;
		font-size: 11px ! important;
		color: #a86210 ! important; }
	h2 { font-weight: bold ! important;
		font-size: 11px ! important;
		color: #a86210 ! important; }*/
	
	.maintitle { font-weight: bold ! important;
		font-size: 22px ! important;
		color: #cccccc ! important;
		line-height: 120% ! important;
		font-family: Trebuchet MS, Verdana, sans-serif ! important;
		text-decoration: none }
	.gen, .poll, td.currentgame, td.bottomgame, td.othergame { font-size: 13px ! important;
		color: #f1f1f1 ! important; }
	.genmed, .name { font-size: 13px ! important;
		color: #f1f1f1 ! important; }
	.gensmall, .postdetails { font-size: 11px ! important;
		color: #f1f1f1 ! important; }
	.mainmenu { font-size: 13px ! important;
		color: #f1f1f1 ! important }
	.cattitle { font-weight: bold ! important;
		font-size: 13px ! important;
		color: #f1f1f1 ! important;
		letter-spacing: 1px ! important; }
	
	/*.catcount { font-weight: normal ! important;
		font-size: 10px ! important;
		color: #9933cc ! important; }*/
	/*use same color as td.cat*/
	.forumlink { font-weight: bold ! important;
		font-size: 13px ! important;
		color: #c8d1d7 ! important; }
	.nav, .topictitle { font-weight: bold ! important;
		font-size: 13px ! important;
		color: #cccccc ! important; }
	.topicread { font-weight: normal ! important; }
	.topicunread { font-weight: bold ! important; }
	.topicunread a, .topicunread a:link, .topicunread a:active, .topicunread a:visited, .topicunread a:hover { color: #cccccc ! important; }

	/*invert these colors depending on the shade of background
	blockquote, .code, pre  {font-size: 13px ! important;
		color: #000000 ! important;
		font-family: Trebuchet MS, Verdana, sans-serif ! important;
		background-color: #c0c0c0 ! important;
		border: #9933cc 1px solid ! important;
		padding-left: 5px ! important;}
	*/
	.pre-error { font-size: 1em ! important;
		color: #000000 ! important;
		font-family: Trebuchet MS, Verdana, sans-serif ! important;
		background-color: #c0c0c0 ! important;
		border: 0px ! important;
		padding-left: 0px ! important; }
	.helpline { border-top-style: none ! important;
		border-right-style: none ! important;
		border-left-style: none ! important;
		background-color: #c0c0c0 ! important;
		border-bottom-style: none ! important; }
	
	/*use same color as input.button background-color*/
	.copyright { font-size: 10px ! important;
		color: #999999 ! important;
		font-family: Trebuchet MS, Verdana, sans-serif ! important;
		letter-spacing: -1px }
	
	/* use same color values as .admin */
	.admin { font-size: 11px ! important;
		color: #a5a077 ! important; }
	.moderatortitle { font-weight: bold ! important;
		font-size: 13px ! important;
		color: #a5a077 ! important; }
	.moderator { font-size: 10px ! important;
		color: #a5a077 ! important; }

/* LINKS */

	/* use same values for each color */
	a:link { color: #a5a077 ! important;
		text-decoration: underline ! important; }
	a:active { color: #a5a077 ! important;
		text-decoration: underline ! important;}
	a:visited { color: #a5a077 ! important;
		text-decoration: underline ! important; }
	a:hover { color: #a5a077 ! important;
		text-decoration: underline }

/* LINES */

	/* use th background-color value */
	hr { border: #7f7949 1px solid ! important;
		height: 0px }
	
	/* use same color as page background-color value */
	.bodyline { border: #7f7949 1px solid ! important;  }
	.forumline { border: #7f7949 1px solid ! important;
		background: #000000 ! important; }

/* TD */
	td.gamecontent 
	{ 
		border-right: 1px solid #000000 ! important; 
		border-top: 1px solid #000000 ! important; 
		border-bottom: 1px solid #000000 ! important;
		background-color: #2c2922 ! important;
		font: normal 10px Trebuchet MS, Verdana, sans-serif ! important; 
		color: #aab6bd ! important;
	}
	td.currentgame
	{
		background-color: #2c2922 ! important;
		border-top: 1px solid #000000 ! important;
		border-bottom: 1px solid #000000 ! important;
		border-left: 1px solid #000000 ! important;
		width: 200px ! important;
	}
	td.othergame_character_first
	{		
		border-left: 1px solid #2c2922 ! important;
		width: 200px ! important;
		font: normal 10px Trebuchet MS, Verdana, sans-serif ! important; 
		color: #a4bfcd ! important;
	}
	td.othergame
	{
		border-top: 1px solid #2c2922 ! important;		
		border-left: 1px solid #2c2922 ! important;
		width: 200px ! important;
	}	
	td.row1 { background-color: #2c2922 ! important; }
	td.row2 { background-color: #2c2922 ! important; }
	td.row3 { background-color: #2c2922 ! important; }
	td.row3right { background-color: #2c2922 ! important; }
	td.row1announce { background-color: #57312a ! important;}
	td.row1sticky { background-color: #31221f ! important; }
	td.row2announce { background-color: #57312a ! important; }
	td.row2sticky { background-color: #31221f ! important; }
	td.row3announce { background-color: #57312a ! important; }
	td.row3sticky { background-color: #31221f ! important; }
	td.rowpic { background-color: #2c2922 ! important }
	td.cat { height: 28px ! important;
		background-color: #e4dccb ! important }
	
	/* use same background-color as td.cat */
	td.cathead { height: 28px ! important;
		background-color: #31221f ! important }
	td.catsides { height: 28px ! important;
		background-color: #31221f ! important }
	td.catleft { height: 28px ! important;
		background-color: #31221f ! important }
	td.catright { height: 28px ! important;
		background-color: #31221f ! important }
	td.catbottom { height: 28px ! important;
		background-color: #31221f ! important }
	td.spacerow { background-color: #2c2922 ! important }
	
	/*
	td.attachrow { font: normal 11px Trebuchet MS, Verdana, sans-serif ! important;
		color : #9933cc ! important;
		border-color : #9933cc ! important; }
	td.attachheader { font: normal 11px Trebuchet MS, Verdana, sans-serif ! important;
		color : #9933cc ! important;
		border-color : #9933cc ! important;
		background-color: #9933cc ! important; }
	td.gamecontent { border: 1px solid #000000 ! important;
		border-left: 0px ! important;
		background-color: #9933cc ! important; }
	*/

/* TH */
	th, .thhead { font-weight: bold ! important;
		font-size: 11px ! important;
		color: #f1f1f1 ! important;
		height: 25px ! important;
		background-color: #000000 ! important }

/* TABLES 
	table.attachtable	{ font: normal 13px Trebuchet MS, Verdana, sans-serif ! important;
		color : #9933cc ! important;
		border-color : #9933cc ! important;
		border-collapse : collapse ! important; }
	table.gametabs { padding: 0px ! important;
		font-size: 13px ! important; }
*/

/* POSTS */
	.postbody { font-size: 13px ! important;
		color: #f1f1f1 ! important; }
	.postbody blockquote {border: 1px solid #7f7949 ! important;
		padding: 10px ! important;
		color: #cccccc ! important;
		margin: 5px 0px 10px 0px ! important;}
	/* use same color value as .postbody */
	.postinfo { font-size: 10px ! important;
		color: #f1f1f1 ! important;
		background-color: #31221f ! important;
		padding: 4px ! important;
		height: 2em ! important; }
	.postinfo .date { width: 160px ! important;
		float: left ! important; }
	.postinfo .subject { float: left ! important; }
	.postinfo .action { float: right ! important; }

/* MISC 
	.bg_yellow { background-color:#9933cc ! important; }
	.moderation_highlight, .bg_small_yellow { background-color: #9933cc ! important; }
	*/
	.mceContentBody { color: #f1f1f1 ! important; }

/* PERSONAS */
	.persona { background-color: #57312a ! important;
		float: left ! important;
		width: 120px ! important;
		height: 100px ! important;
		border: 1px solid #7f7949 ! important;
		margin: 3px ! important;}
	.personaSelect { background-color: #31221f ! important;
		width: 100% ! important;
		margin: 0px ! important;
		padding: 2px 0px ! important;
		position:absolute ! important;
		bottom:0 ! important;
		left:0 ! important;
		text-align: center ! important;}
	.floating { height: 100% ! important;
		position: relative ! important; }
	.guildName { font-size: 11px ! important; }

}
