css:
/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml); @-moz-document domain("dobrochan.ru"),domain("dobrochan.org"),domain("dobrochan.com") {
.logo a,.logo .description,.logo br,.logodobleft,.logodobright,.theader,body[onload*="update"] hr,body>hr,.stylebar,#jquery_jplayer,.doubledash,.fileinfo br~br,.cpanel~.fileinfo br,.adminbar:first-child,#hideinfodiv hr,.subtitle,#music_player {display: none !important;}
body {font-size: 16px !important; margin: 25px 8px 8px !important; padding: 8px /* 30px*/ !important; background: transparent !important;} body>img[style*="moz-zoom-in"] {max-width: 100% !important; height: auto !important;}
#delete_form {padding: 8px 22px !important;}
.olddefaultimagestyle-body {padding: 0 !important;}
html {background: white repeat-x !important; background-image: url("https://lh5.googleusercontent.com/-9mPfq_bTK4o/T4qBmWVF8eI/AAAAAAAAAMQ/ZojfswVTVrQ/I/backgrnd777.jpg"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAVCAYAAABsZT8HAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAANsAAADbABfWVZ+gAAAAd0SU1FB9oGAxEqHfMNphEAAAA5SURBVAjXY2SQUvnPwMDAwMLAyMQAYTAwQhmMDAwMDIzIUoyMOBkMmFIk6SJCMYxRH+rKwMDAwAAAGzcCz5PMGZsAAAAASUVORK5CYII=") !important; background-position: center 21px,0 0 !important; /*overflow-y: scroll !important;*/ overflow: visible !important;}
body,html,.reply,.highlight,.popup,.logo,.post-error {color: black !important;}
body[onload*="update"] {padding: 155px 0 0 !important;}
body[onload*="update"] a {text-decoration: none !important; color: #603344 !important; background: none transparent !important;}
body[onload*="update"] a:hover {text-decoration: underline !important;}
body[onload*="update"] .category,.newssize {border: 1px solid #A6A697 !important; background: rgba(255,255,255,0.4) !important; margin: 2px 0 !important; padding: 2px 4px !important; border-radius: 5px !important;}
.category {font-size: small !important;}
.newssize {padding: 25px 45px 55px 40px !important; margin: 9px 0 !important;}
td,.pages {border: 0 none !important; background: none transparent !important; padding: 0 !important;}
td.header {font-weight: bold; color: #600000 !important;}
body[onload*="update"] .logo {position: absolute !important; top: 25px !important; left: -5px !important;}
.category:last-of-type .header,.adminbar~.logo,#hideinfodiv,#hideinfotd {display: block !important; position: absolute !important; font-weight: bold !important; top: 0 !important; left: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; text-decoration: none !important; line-height: 21px !important; padding: 0 8px !important; width: auto !important;}
#hideinfodiv,#hideinfotd {left: auto !important; right: 0 !important; top: 0 !important; font-weight: normal !important;  line-height: 21px !important;} #hideinfodiv[style*="none"],#hideinfotd[style*="none"],#replyform[style*="none"] {display: none !important;}
#hideinfodiv a,#hideinfotd a {color: #ccc !important; padding: 0 8px !important; font-weight: bold !important;} #hideinfodiv a:hover,#hideinfotd a:hover {color: white !important;}
.adminbar {float: none !important;}
.featuredimgtop,.featuredimgbottom,.newstop,.newsbottom,.newsbg {background: none transparent !important;}
.file {float: left !important; margin: 2px 20px !important;}
.replytitle {color:#002244 !important;}
.spoiler,.spoiler blockquote {display: inline !important;}
.spoiler blockquote:after,.spoiler blockquote:before,div.spoiler:after,div.spoiler:before {content: "\A"; white-space: pre-wrap;}
.spoiler,.spoiler blockquote {background: black !important; color: transparent !important;}
.spoiler * {background: inherit !important;}
.spoiler:hover {background: black !important; color: white !important;}
.spoiler:hover *,.spoiler a {color: inherit !important;}
.spoiler:hover blockquote {color: #dcd !important;}
.delete,.delete-file {background: transparent url("http://dobrochan.ru/images/delete.png") 0 0 no-repeat !important;}
.delete input {display: none !important;}
.delete>img {display: inline-block !important;}
.delete.checked,.delete-file.checked {background-image: url("http://dobrochan.ru/images/delete_selected.png") !important;}
.hide {background-image:url("http://dobrochan.ru/images/hidethread.png") !important; background-color: white !important; border-radius: 1em !important;}
.reply_ {background-image:url("http://dobrochan.ru/images/reply.png") !important;}
.edit_ img,.search_google img,.search_iqdb img {display: none !important;}
.edit_,.search_google,.search_iqdb {background: none transparent !important; text-decoration: none !important; border: 0 none !important;}
.edit_:after {content: "[edit]" !important;} 
.search_google:after {content: "[g]"; }
.search_iqdb:after {content: "[iqdb]";}
.edit_:after,.search_google:after,.search_iqdb:after {text-decoration: none !important; color: gray !important; font-size: 11px !important;}

a {color: #034 !important; margin: 0 !important; padding: 0 !important;}
h2 {color: black !important;} .postertrip {color: #567 !important;} .fileinfo,.abbrev {color: #666 !important;}
.reply,#delform .reply,.popup {background: none #eee !important; border: 1px solid #ccc !important; border-radius: 5px !important; box-shadow: none !important;}
.reply,.highlight {padding-right: 4px !important;}
.reply .fileinfo,.reply .file {background: none #eee !important;}
.fileinfo {font-size: 0.8em !important; margin: 0 20px 2px !important; text-align: left !important;}
.file .fileinfo {margin: 0 0 2px !important;}
.highlight,#delform .highlight {background: #ddd !important; border: 1px solid #A6A697 !important; padding: 0 !important; margin: 0 !important; border-radius: 5px !important;}
.post {margin: 0 !important; background: transparent !important;}
blockquote {color: #539 !important; padding: 0 !important;}
.postbody {margin: 5px 5px 0 !important; padding: 0 !important;}
.filetitle {color:#002244 !important; font-size:18px !important;}
.postername {color:#004A99 !important;}
.postername,.replytitle,.postblock {font-weight: bold !important;}
.reflink {font-size: 0.8em !important; font-weight: bold !important;}
.reflink a {color: black !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;}
select input,.postnode {border: 0 none !important;}
input,textarea,select,button,.postblock {-moz-appearance: none !important; color: black !important; font-size: 12px !important; border: 1px solid #ccc !important; background: #eee !important;}
input[type="checkbox"] {border: 1px solid #A6A697 !important; background-color: white !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}
input[type="button"] {margin: 1px !important;}
#txt_btns input {margin: 2px 1px 0 !important;}
#postform,.document {/*float: right !important;*/ display: inline-block !important; padding: 10px 20px !important; margin: 0 0 15px !important; background: rgba(255,255,255,0.4) !important; border: 1px solid #A6A697 !important; border-radius: 5px !important; width: auto !important;}
.postblock {padding: 1px !important;}
pre {white-space: pre-wrap !important;} code:after {white-space: pre-wrap !important; content: "\A";}
.cpanel .reply_:before {content: ">>";}
.cpanel .reply_ img {display: none !important;}
.cpanel .reply_ {width: auto !important; background: none transparent !important;  font-size: 10px !important; font-weight: bold !important; color: gray !important;}
.thread:only-of-type {counter-reset: list 1;}
.thread:only-of-type>table .reflink+.cpanel:before {counter-increment: list; content: counter(list);
color: #4f7942; font-style:italic; font-weight: bold !important; font-size: 0.8em !important;}
}
