 top: -6px !important;
 left: 10px !important;
}

.compressed.link .arrow.up, .compressed.link .arrow.upmod {
 position: inherit !important;
 top: auto !important;
 left: auto !important;
}
.compressed.link .arrow.down, .compressed.link .arrow.downmod {
 position: inherit !important;
 top: auto !important;
 left: auto !important;
}
/*---------------------------------------------------------------------------*/

.link .entry {
 padding: 0 5px 0 5px !important;
}

.link .title {
 font-size: 100% !important;
 margin: 0 !important;
 padding: 0 !important;
}

.link a.title {
 font-size: 115% !important;
 margin: 0 !important;
 border-bottom: 1px solid #75ABEA !important;
 font-family: arial,sans-serif !important;
 background-color: transparent !important;
 text-decoration: none !important;
 font-weight: bold !important;
 letter-spacing: -0.01em !important;
}

.link p.title span.domain {
 color: #999999 !important;
}

.link p.tagline {
 color: #999999 !important;
}

.domain a, .tagline a {
 border-bottom: 1px solid #DDDDDD !important;
 color: #777777 !important;
 text-decoration: none !important;
}

.domain a:hover, .tagline a:hover, .entry .buttons li a:hover {
 color: #000000 !important;
}

.tagline {
 padding: 1px 0 2px 0 !important;
}

.subreddit {
 text-transform: capitalize !important;
}

/* Entry Buttons
-----------------------------------------------------------------------------*/
.entry .buttons li {
 color: red !important;
 font-size: 11px !important;
}

.entry .buttons li a {
 color: #578CCA !important;
 text-decoration: none !important;
 font-weight: normal !important;
 padding-bottom: 2px !important;
}

a.comments {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 1px;
 padding: 0 0 0 17px !important;
}

ul a.option {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -17px;
 padding: 0 0 0 17px !important;
}

.save-button {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -35px;
 padding: 0 0 0 15px !important;
}

.unsave-button {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -51px;
 padding: 0 0 0 15px !important;
}

.hide-button {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -67px;
 padding: 0 0 0 15px !important;
}

.unhide-button {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -83px;
 padding: 0 0 0 15px !important;
}

.link .report-button, .link .report-button {
 position: relative !important;
 background: url(data:image/gif;base64,)
 no-repeat 0 -99px;
 padding: 0 0 0 15px !important;
}
/*---------------------------------------------------------------------------*/

#newlink {
 top: 0 !important;
}

.expando-button {
 margin: 7px 5px 0 0 !important;
}

.content.preftable {
 top: 0 !important;
}

.subreddit .description {
 font-size: inherit !important;
 max-width: none !important;
}

