css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://forum.gamescampus.eu") {
  div.container { width: 967px !important; }
  div.left_wrap { display:none }
  div.content_wrap { overflow: auto !important; width: 100% !important; }
  div.title { width: 99% !important; }
  div.forum_wrap { width: 100% !important; }
  ul.nav li { float: none !important; }
  table.tablebg { background-color: #EEE !important; }
  div.page { background-color: #AAA !important; }
  div.postbody { line-height: 115% !important; }
  div.postbody ol { margin-left: 0 !important; }
  div.postbody li { font-size: 1.3em !important; }
  div.quotecontent { font-size: 12px !important; background-color: #ddd !important; }
}
