/*===============================================*/
@-moz-document domain("youtube.com") {

/* All Starts Here */
/*===============================================*/


/*===============================================*/
/* Always Show Long Descriptions */
/*
SPAN[id^="RemainvidDesc"]
 { display: inline !important; }

SPAN[id^="MorevidDesc"],
SPAN[id^="LessvidDesc"]
 { display: none !important; }
*/
/*===============================================*/


/*===============================================*/
/* Links and Chains */
a,
a:active,
a:link,
a:visited
 {
 color: #48f !important;
 }

[class="blacktext"],
[class="commHeading"],
[class="dg"],
[class="eLink"],
[class="emptySetTitle"],
[class="expandLink"],
[class="grayText"],
[class="headerTitle hpBlockHeadingGray"],
[class="largeTitles"],
[class="name"],
[class="name floatL"],
[class="navHead channelsColor"],
[class="navHead videosColor"],
[class="runtime"],
[class="smallText"] b,
[class="smallText grayText"],
[class="title floatL"],

[id="catIndexGridHeading"],
[id="hpEditorHead"],
[id="hpVideoListHead"]
 {
 color: #8dd !important;
 }
/*===============================================*/


/*===============================================*/
#masthead #search-button,

input,
select,
textarea,

[class="confirmBox"],
[class="pagerCurrent"]
 {
 color: #ccc !important;
 background-color: #001 !important;
 }
/*===============================================*/


/*===============================================*/
.label,
.requirements,
.smgrayText,

b,
body,
strong,

[class="bulletinTable"],
[class="emptySetContent"],
[class="emptySetContentLg"],
[class="extraVertSpaceMini"],
[class="footLabel"],
[class="formFieldInfo"],
[class="hpBlockHeading hpBlockHeadingGray"],
[class="label"],
[class="lightLabel"],
[class="normalLabel"],
[class="profileTitles"],
[class="smallLabel"],
[class="smallText"],
[class="statLabel"],
[class="subhead"],
[class="title"],
[class="viewCount"],

[id="commentsDiv"] h2,
[id="videoDetailsHeader"]
 {
 color: #bbb !important;
 }
/*===============================================*/


/*===============================================*/
/* SnakePit */
#masthead .nav-item .dropdown ul,
#masthead .nav-item .dropdown ul li,

.highlightBox,
.hpPurpleBlock,

IMG,
map,

[class="BoxesInnerOpacity"],
[class="button_bucket"],
[class="channelMastheadTable"],
[class="channelMastheadTable"] img,
[class="content"],
[class="first"],
[class="form_fieldset"],
[class="form_fieldsets"],
[class="highlightBox floatR bold"],
[class="highlightBoxesOpacity"],
[class="vGridBox"],
[class="marT20 alignC"],
[class="memberContainer"],
[class="notopbox"],
[class="tabpad"],

[id="baseDiv"],
[id="channelVidsDiv"],
[id="custom_player_form"],
[id="hpFeaturedAndSmallTabs"],
[id="notopbox"],
[id="thisVidDiv"] td,
[id="vResponseHeading"],
[id="vrParentDiv"]
 {
 background-color: #001206 !important;
 }
/*===============================================*/


/*===============================================*/
/* Image Borders */
.form-noident,

div img,
img,

[class="channelIcon"],
[class="v90WrapperInner"],
[class="video-thumb-medium"],
[class="videoIconWrapperInner"]
 {
 background-color: #000 !important;
 border-color: #960 !important;
 }
/*===============================================*/


/*===============================================*/
/* DarkGreen Fields */
[class="changeSearchTypeLinks"],
[class="highlightBox marB20"],
[class="hpContentBlock"] img,
[class="hpContentBlock padT10"] img,
[class="hpPurpleBlock"],
[class="hpPurpleBlock alignC"],
[class="hpSideBlock padT10"] img,
[class="hpYellowBlock"],
[class="pagingDiv"],
[class="spOffersDiv"]
 {
 background-color: #001206 !important;
 background-image: none !important;
 border-color: #001206 !important;
 }
/*===============================================*/


/*===============================================*/
/* BlueGrounds */
.headerBoxOpacity[class],

[class="cat-spotlight-box"],
[class="codeBox floatR marL15"],
[class="commentHead"],
[class="headerBox"],
[class="headerBoxOpacity"],

[id="footContent"],
[id="localePickerBox"],
[id="loginForm"],
[id="shareSpan"],
[id="signupForm"],
[id="statsBox"]
 {
 background-color: #000018 !important;
 }
/*===============================================*/


/*===============================================*/
/* BlackGrounds */
body,

[class="hilite"],

[id="searchDiv"]
 {
 background-color: #000 !important;
 border-color: #000 !important;
 }
/*===============================================*/


/*===============================================*/
/* All Ends Here */
}
