css:
@-moz-document domain('xbox-360.co.za'), url-prefix('http://xbox-360.co.za/') 
{
	* {-moz-box-shadow:none !important;}
	html {background:url("http://www.xbox-360.co.za/forum/images/xbox/doc-bg.png") repeat-x scroll 0 0 #3E3E3E !important;}
	body {font-family:Helvetica, Arial, Verdana, Tacoma !important;}
	.body_wrapper {background:none repeat scroll 0 0 #3E3E3E !important;}
	
	.newcontent_textcontrol {-moz-box-shadow:none !important;border:1px solid #666666;}
    
	.content a, .signaturecontainer a {color:#7FAD2A !important;font-weight:bold !important;}

	.breadcrumb, .breadcrumb ul li a {color:#FFFFFF !important;}
	.breadcrumb .navbit a:hover {background-color:#666666 !important;}
	.threadtitle a {color:#FFFFFF !important;}
	.inner .threadtitle a {color:#000000 !important;}

	.postcontainer {border: 1px solid #333333 !important;}
	.posthead {background:none repeat scroll 0 0 #7FAD2A !important;border:none !important;}
	.postbody {background:none repeat scroll 0 0 #DDDDDD !important;}
	.postcontrols div a img {margin-top:-2px !important;}
	.postbit .postrow {padding: 5px 10px 10px !important;}
	h2.posttitle {padding:10px 0 !important;}
	.postfoot{border-top:1px solid #AAAAAA !important;}
	.postcontent a img {border:1px solid #AAAAAA !important;}
	
	.userinfo{border-bottom:1px solid #AAAAAA !important;border-top:1px solid #AAAAAA !important;}
	.contact {width:550px !important;}
	.userinfo_extra {display:none !important;}
.userinfo_extra dl {width:140px !important;}
	.username_container {float:none !important;}
.username_container div {display:none;}
	.rank {display:none !important;margin-bottom:5px;vertical-align:top;}
	.usertitle {display:inline-block !important;width:250px;}

	

	.postbit_reputation {margin-left:5px;text-align:left !important;width:100px;}
.repimg {margin-left:-4px !important;}
.memberaction a strong span {display:inline-block;}


.smallavatar img{margin:2px;}

.postfoot div, .userinfo_noavatar, .userinfo, .textcontrols .floatcontainer, .postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a {background-color:#BBBBBB !important;}
.postcontrols{width:70% !important;}

	.signaturecontainer {border-top:1px solid #888888 !important;padding-top:15px !important;}
	.content {margin:0 0 10px !important;}

#sidebar_container  {margin-top:25px !important;}
	#block_html_3, #block_html_6, #ad_global_above_footer {display:none !important;}

.postbit .postbitim .postcontainer {margin-bottom:0px !important;}
}
