@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://m.jeuxvideo.com/forums/") {

/*____________________________La liste des topics */

#liste_topics .ltopic .pseudo {
  float: right !important;
  position: relative !important;
  top: -22px !important;
  font-size: 120% !important;
  right: 170px !important;
}

#liste_topics .ltopic .pseudo:after {
  content: "|" !important;
  margin-left: 25px !important;
  color: rgba(0,0,0,.9) !important;
}

#liste_topics .ltopic .dt {
  font-size: 110% !important;
  font-style: italic !important;
  top: 8px !important;
  margin-right: 6px !important;
}

#liste_topics .ltopic strong span {
  position: absolute !important;
  left: 940px !important;
}

#liste_topics .ltopic strong span:after {
  content: "|" !important;
  position: absolute !important;
  left: 100px !important;
  color: rgba(0,0,0,.9) !important;
  font-size: 120% !important;
}

.pseudo.moder {color: rgb(170,0,0) !important;}

#liste_topics ul li[class] {
  background-color: rgb(255,230,230) !important;
  border-top: solid 1px rgb(255,40,40) !important;
  border-left: solid 1px rgb(255,40,40) !important;
  border-right: solid 1px rgb(255,40,40) !important;
}

#liste_topics li:not([class]) {
  border-top: 1px solid rgb(255,150,150) !important;
  border-left: 1px solid rgb(255,150,150) !important;
  border-right: 1px solid rgb(255,150,150) !important;
}

#liste_topics h3 {
  background-color: rgb(100,0,0) !important;
}

#liste_topics ul {
  border-bottom-color: rgb(100,0,0) !important;
}

#liste_topics a strong, #post a {
  color: rgb(150,0,0) !important;
}

#liste_topics a:visited strong, #post a:visited {
  color: rgb(250,50,50) !important;
}

#liste_topics a:hover strong, #post a:hover {
  color: rgb(200,25,25) !important;
}
/* Fin des topics */


/*______________________ce qui est dans toutes les pages / Ce qui n'est pas les topics/mess */

.titre_forum {
  background-color: rgb(255,75,75) !important;
  border: 1px solid red !important;
  border-bottom: 0px !important;
}
.titre_forum a {
  color: rgb(150,0,0) !important;
}

div.sujet_forum {
  border: 0px solid red !important;
}

div.rech {
  border: 1px solid red !important;
  background-color: rgb(255,230,230) !important;
  border-radius: 0px 0px 5px 5px !important;
}

.rech form label {
  color: rgb(125,0,0) !important;
}

.banner + .sujet_forum tr td {
  background-color: rgb(255,220,220) !important;
  border-radius: 7px !important;
}

.banner {display:none!important}
/* fin */

/* ____________________Tout ce qui est message & all */

.titre_forum + div.sujet_forum h2 {
  border: 1px solid red !important;
  background-color: rgb(255,230,230) !important;
  border-radius: 0px 0px 5px 5px !important;
}
div.sujet_forum h2 a span {
  color: rgb(175,0,0) !important;
}
div.sujet_forum h2 a span:after {
  content: " : " !important;
}

.navig_msg td {
  color: rgb(255,100,100) !important;
}
.navig_msg td[class="on"] {
  color: white !important;
  background-color: rgb(255,130,130) !important;
}
.navig_msg td a {
  color: red !important;
}

#list_msg ul.m2 {
  background: rgb(255,240,240) !important;
  border-color: rgb(255,175,175) !important;
  box-shadow: 2px 2px 12px rgb(255,160,160) inset !important
}

#list_msg ul .suite {
  position: absolute !important;
  right: 20px !important;
  top: 10px !important;
  visibility: hidden !important;
}

#list_msg ul .suite:after {
  content: "[Lire le post en entier !]" !important;
  visibility: visible !important;
  font-size: 150% !important;
  color: blue !important;
}

.sujet_forum {
  background-color: rgb(255,220,220) !important;
}

.hautpage a {
  color: rgb(175,0,0) !important;
}

#dix_msg, #poster h3 {
  color: rgb(200,0,0) !important;
}

}
