css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://members.easynews.com/) {

.poster { display: none !important }

.header { display: none !important }




a { color: #3366ff !important; }

a:hover {
	color: white !important;
	background-color: transparent !important;
}



.group {
	letter-spacing: 0px !important;
	font-size: 10px;
}

table {
color: #eeeeee !important;
	background-color: black !important;
    background-image:url("data:image/png;base64,") !important;
    background-repeat: repeat-x ! important;
}

body {
	font-size: 14px;
	color: #eeeeee !important;
	background-color: black !important;
    background-image:url("data:image/png;base64,") !important;
    background-repeat: repeat-x ! important; 
    background-attachment: fixed ! important;
}

select, option, input {
	color: #bbbbbb !important;
	background-color: black !important;
border: 1px solid #333333 !important; 
}

input { -moz-border-radius: 10px !important; padding: 0px 5px !important; }

input[name="fil"] { width: 205px !important; }

option { border: 0px !important; }

.rRow1 { background: #222222 !important; }
.rRow2 { background: #111111 !important; }

.fSize, .timeStamp { color: #cccccc !important; }

}
