css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bt-chat.com") {

.date, .tbox, .newstop, .switchcontent, .menumid, .menubottom, #news, .newsbottom, .menutop, .menutop2, .thickbarmid, .thinbarmid, form a, .pageprevlink, .pagenextlink, .pageprevlink, .pagenextdead, .pageprevdead, .pagenumdead,
td.newsmid table  td table td  {
	display:none !important;
}

td.newsmid table.t540  td table td { display : table-cell;}

body {
	font-size: 16px !important;
	background: #fff !important;
	color:#47525c !important;
	text-align: center !important;
    margin:0 !important;
}

img { border:none !important;}


td,th {
	font-size: 16px !important;
	margin: 0 !important;
	background: #fff !important;
	color : #555;
}

th { color:#fff !important;}

.bb { color:#fff !important;}

table {
	text-align: center !important;
	border-collapse:collapse !important;
	border:none !important;
}

.ul {text-decoration: none !important;}

a:link, a:visited {
	color: #5874A3 !important;
	text-decoration: none !important;
}

a:hover, a:focus, a:active {
	text-decoration: underline !important;
	color: #000000 !important;
}

tr.off {
	font-size: 16px !important;
	background: #353535 !important;
}

tr.on {
	font-size: 16px !important;
	background: #eee !important;
}

.bottom { border-bottom: 1px dotted #999 !important; }
.top { border-top: 1px dotted #999 !important; }
.right { border-right: none !important;}
.left {	border-left: none !important; }

.all td {
	font-size : 0.8em !important;
	height : 40px !important;
}


input {
	background: #f5f5f5 !important;
	border: 5px solid #ccc !important;
	color: #999 !important;
	font-weight: 700 !important;
	font-size : 1.5em !important;
	padding : 8px 0.3em !important;
	letter-spacing : -1px !important;
}

input:hover, input:focus ,select:hover, select:focus {
	background: #eee !important;
	border: 5px solid #71cdff !important;
	color: #5874A3  !important;
}

input[type=text] {
	letter-spacing : -2px !important;
	padding : 9px 0.3em !important;
}


.buttonBarright {
  text-align: right !important;
  vertical-align: top !important;
  margin: 0 !important;
  padding: 4px 0 !important;
}

table, .center {margin : 0 auto !important;}

.aRight {
	text-align: right !important;
	margin-left: auto !important;
	margin-right: 0 !important;
}

.aLeft {
	text-align: left !important;
    margin-left: 0 !important;
    margin-right: auto !important;
}

.leftI {
    margin-left: 30px !important;
	margin-right: auto !important;
}

.pagenumlink {
        border: none !important;
        color: #71cdff !important;
        font-size: 12px !important;
}

.menumid {
    background:#fff !important;
	vertical-align:top !important;
}

.menubottom {
    background:#fff !important;
	width:147px !important; 
	height:20px !important;
}

.newsmid {
	background:#fff !important;
	color : #444 !important;
	vertical-align:top !important;
}

.t750 { width:940px !important;}
.t375 { width:375px !important;}
.t350 { width:350px !important;}
.t540 {	width:540px !important;}
.c150 {	width:150px !important;}
.c300 { width:300px !important;}
.c350 { width:350px !important; }
.c100 {	width:100px !important; }
.c600 {	width: 600px !important; }
.c450 {	width: 450px !important;}
.c45 { width: 45px !important;}
.c40 { width: 40px !important;}
.c250 {	width: 250px !important;}
.c400 {	width:400px !important;}
.c85 { width:85px !important;}
.c225 {	width:225px !important;}
.c650 {	width:650px !important;}
}
