css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.fok.nl") { 

/* edit-tekst */
.edit { padding-left: 10px!important; font-size: 10px!important; color: #666666!important}

/* Topic-lijsten */

.atlistLP, .atlistT, .atlistSt, .atlistP, .atlistV, .atlistS, .atlistA, .TlistLP, .TlistT, .TlistTs, .TlistP, .TlistV, .TlistS, .TlistA {border-bottom: 1px solid #aaaaaa !important; line-height: 20px !important}
.atlistLP a, .atlistSt a, .atlistP a, .atlistV a, .atlistS a, .atlistA  a , .TlistLP a, .TlistT a, .TlistTs a, .TlistA a{ color: black !important; font-size: 11px !important; text-decoration: none !important}
.atlistT a, .TlistT a  {font-size: 12px !important; font-weight: bold !important; text-decoration: none !important; color: black !important; overflow: hidden !important;}
.atlistT , .TlistT {width: 370px!important} 
.atlistT div, .TlistT div {width:365px!important}
.atlistSt, .TlistTs, .forumTs, #atSt {width: 90px !important}
.atlistLP, .TlistLP {width: 150px !important}
.atlistS, .TlistS {color: red !important; font-size: 12px !important}
a:hover {text-decoration: underline!important}

/*image resizer */
.tdpostone img {max-width: 580px !important}
.tdposttwo img {max-width: 580px !important}

}
