css:
/*4chan.org Grey on Black -style.abcmod*/
/*Made by Defu/Defree on 21th November 2006, edited by abc Nov 2007*/
/*Last edited Nov 2007*/
/*I made this for my own purposes, so feel free to modify it to suit yours.*/
/*I didn't really spend much time on formatting the code to look all nice and shiny either ;)*/

@namespace none;
@-moz-document domain("4chan.org") {
   body {
      background-color: #000000 ! important;
      color: #CCCCCC ! important;
      font-size: 12pt ! important;
      padding-left: 5px ! important;
      padding-right: 5px ! important;
      margin-right: 0px ! important;
      margin-left: 0px ! important;
      margin-top: 5px ! important;
   }

   a {
      background: inherit ! important;
      color: #cccccc ! important;
      font-family: serif ! important;
   }

   a:visited {
      background: inherit ! important;
      color: #FFFF99 ! important;
      font-family: serif ! important;
   }

   a:hover {
      background: #000000 ! important;
      color: #FF0000 ! important;
      font-family: serif ! important;
   }

   a.quotelink {
      background: inherit ! important;
      color: #555555 ! important;
      font-family: serif ! important;
   }

   a.quotelink:hover {
      background: #bbbbbb ! important;
      color: #555555 ! important;
      font-family: serif ! important;
   }

/*Front page and Menu stuff*/
   h1, h2, h3 { 
      margin: 0px ! important;
      background: #999999 ! important;
      color: #000000 ! important;
   }
 
   .plus { 
      background: inherit ! important;
      float: right ! important;
      font-size: 8px ! important; 
      font-weight: normal ! important; 
      padding: 1px 4px 2px 4px ! important; 
      margin: 0px 0px ! important;
      background: #999999 ! important; 
      color: #000000 ! important; 
      border: 1px solid #000000 ! important;
      cursor: pointer ! important; 
   }

   .plus:hover { 
      background: inherit ! important;
      border: 1px solid #777777 ! important; 
   }

   #dis, #img, #draw, #up {
      font-size: 80% ! important;
      font-family: courier ! important;
   }



/*Thread pages*/
   .logo {
      background: inherit ! important;
      color: #aaaaaa ! important;
      font-size: 24pt ! important;
      font-weight: bold ! important;
      text-align: center ! important;
      clear: both ! important;
      width: 100% ! important;
   }

   .postarea {
      background: inherit ! important;
   }

   .rules {
      font-size: 10px ! important;
      width: 468px ! important;
   }

   .rules, .rules a, .rules a:visited, .rules a:link {
      font-family: sans-serif ! important;
   }

   .rules li {
      text-indent: -1em ! important;
      margin-left: 1em ! important;
   }

   .postblock { /* Posting form */
      background: #888888 ! important;
      color: #000000 ! important;
      font-weight: 800 ! important;
   }

   .footer {
      font-size: 12px ! important;
      font-family: serif ! important;
      text-align: center ! important;
   }

   .unkfunc {
      color: #33CC00 ! important;
      font-style: italic ! important;
   }

   .filesize {
      font-size: 16px ! important;
      font-family: serif ! important;
      text-decoration: none ! important;
   }

   .filetitle {
      background: inherit ! important;
      color: #bbbbbb ! important;
      font-size: 20px ! important;
      font-family: serif ! important;
      font-weight: 800 ! important;
   }

   .postername {
      background: inherit ! important;
      color: #999900 ! important;
      font-size: 20px ! important;
      font-family: serif ! important;
      font-weight: 900 ! important;
   }

   .postertrip {
      background: inherit ! important;
      color: #FF99FF ! important;
      font-size: 16px ! important;
      font-family: serif ! important;
   }

   .oldpost {
      background: inherit ! important;
      color: #666666 ! important;
      font-family: serif ! important;
      font-weight: 800 ! important;
   }

   .omittedposts, .abbr {
      background: inherit ! important;
      color: #777777 ! important;
   }

   .reply { /* Reply posts */
      background: #242323 ! important;
      color: #CCCCCC ! important;
      font-family: serif ! important;
      border: 0px solid #666666 ! important;
      height: auto ! important
   }

   .replyhl { /* Focused reply */
      background: #CCCCFF ! important;
      color: #000000 ! important;
      font-family: serif ! important;
      font-weight: 500 ! important;
      border: 1px solid #3333FF ! important;
      height: 100% ! important
   }

   .doubledash {
      vertical-align: top ! important;
      clear: both ! important;
      float: left ! important;
      visibility: hidden ! important;
   }

   .replytitle {
      background: inherit ! important;
      font-size: 18px ! important;
      font-family: serif ! important;
      color: #FFCC00 ! important;
      font-weight: 800 ! important;
   }

   span.commentpostername {
      background: inherit ! important;
      font-size: 18px ! important;
      font-family: serif ! important;
      color: #336666 ! important;
      font-weight: 800 ! important;
   }

   .linkmail, .linkmail:hover, .linkmail:visited, .linkmail:active {
      background: inherit ! important;
      font-size: 16px ! important;
      font-family: arial ! important;
      color: #663366 ! important;
      font-weight: 800 ! important;
      margin-left: -4px ! important;
      padding-left: 6px ! important;
      text-decoration: underline ! important;
   }

   a.quotejs:active,a.quotelink:link,a.quotejs:visited {
      background: inherit ! important;
      color: #33CCFF ! important;
   }

   a.quotejs:hover {
      background: inherit ! important;
      font-weight: bold ! important;
   }

   .tn_thread {
      background: #000000 ! important;
      text-align: center ! important;
      margin: 0px 20px 20px 20px ! important;
      float: left ! important;
      border: #98e 1px solid ! important;
      width: 200px ! important;
      height: 100px ! important;

   }

   .tn_reply {   
      background: #999999 ! important;
      text-align: center ! important;
      margin: 0px 20px 20px 20px ! important;
      float: left ! important;
      border: #98e 1px solid ! important;
      width: 100px ! important;
      height: 100px ! important;
   }

   .adHeadline {
      color: #34345C ! important;
      font: bold 10pt serif ! important;
      text-decoration: underline ! important; 
   }

   .adText {
      color: #000000 ! important;
      font: normal 10pt serif ! important;
      text-decoration: none ! important;
   }

   #ad {
      font-family: arial, helvetica, sans-serif ! important;
      font-size: 11px ! important;
      margin: 0 ! important;
      padding: 0 ! important;
      position: absolute ! important;
      right: 1em ! important;
      border: 1px solid #000000 ! important;
      width: 120px ! important;
   }

   #ad div {
      margin: 0 ! important;
      padding: 0.4em ! important;
   }

   #ad div.ad-title { /*The ad at the right*/
      background: #999999 ! important;
      color: #000000 ! important;
      font-size: 11px ! important;
      padding: 0em ! important;
   }

   #ad div.ad-title a {
      color: #000000 ! important;
      font-family: arial,helvetica, sans-serif ! important;
   }

   #ad div.ad-text a {
      font-family: arial,helvetica, sans-serif ! important;
   }

   .spoiler a.quotelink, .spoiler .unkfunc {
      color: inherit ! important;
   }

   .exif {
      display: none ! important;
   }

   .exif, .exif td {
      font-family: arial,helvetica, sans-serif ! important;
      font-size: xx-small ! important;
   }

   .exif td b {
      font-size: x-small ! important;
      font-weight: 800 ! important;
      text-decoration: underline ! important;
   }

   #header {
      position: absolute ! important;
      left: 5px ! important;
      right: 5px ! important;
   }

   * html #header { /* ie only */
      width: 100% ! important;
   }

   #navtop { 
      left: 0px ! important; 
      float: left ! important;
      font-size: 11pt ! important; 
   }

   #navtopr { 
      right: 0px ! important; 
      display: block ! important;  
      text-align: right ! important;  
      font-size: 11pt ! important;  
   }
}
@-moz-document url-prefix("") {
   .replyhl a:link {
      color: #222222 ! important;
   }

   .replyhl a:visited {
      color: #555555 ! important;
   }

   .replyhl a:hover {
      background: #888888 ! important;
   }

   .commentpostername {
      color: #000066 ! important;
   }

}
