/*******************************************/
/*********** Spiralinear KISS **************/
/********* Created by: tjwoosta ************/
/*******************************************/

/********************************************
 
To change the colors of this style use your
text editors global search and replace
feature to replace the following colors.

#262626 (background color)
#3e3e3e (border color)
#8a8a8a (text color)
#a7a15e
#a67b07
#556b2f
#af5f00
#705b46

*********************************************/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spiralinear.org") {


/****************** Main *******************/


* {
background: transparent !important;
border-color: #3e3e3e !important;
color: #8a8a8a !important;
box-shadow: none !important;
text-shadow: none !important;
}

html {
background: #262626 !important;
}


/***************** Text ********************/


a:link,
.Title:visited,
#Menu *,
.Message a[href*="."],
.PanelInfo a,
.Meta .Category,
.CategoryList .Read a:hover,
.CategoryList .Unread a:hover {
color: #a7a15e !important;
}

.Title,
.Title:link,
.Author a,
.PanelInfo a:visited,
.CategoryList .Read a {
color: #556b2f !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h4,
h4 *,
h5,
h5 *,
h6,
h6 *,
.Box h4 a,
dl.icon dd,
a:hover,
.Title:hover,
.StatBox a:hover,
.Tabs a,
.ItemHeading,
a.BigButton,
.Button,
.LastCommentBy a,
.LastDiscussionTitle a[href*="discussion"] {
color: #a67b07 !important;
}

b,
b *,
strong *,
strong,
.Announcement,
.Closed,
.Tabs .SubTab,
.Tabs .SubTab a,
.PanelInfo .Active a,
.CategoryList .Unread a {
color: #af5f00 !important;
}

.Tabs span,
.Count,
.LastCommentDate,
.DateCreated a,
.StatBox,
.StatBox a,
.Votes *,
.DiscussionCount {
color: #705b46 !important;
}

#Head .Menu .Title:before {
content: "Â§piralinear" !important;
font-size: 3em !important;
font-family: monospace !important;
}


/***************** Tweaks ******************/


input,
button,
select,
textatea {
background: #262626 !important;
border: 1px solid #3e3e3e !important;
}

.Overlay #Popup .Border {
border: 5px solid #3e3e3e !important;
border-radius: 0px !important;
background: #262626 !important;
}

.Quote {
border: 1px solid #3e3e3e !important;
}

.Message pre {
max-height: 400px !important;
overflow: auto !important;
}

.Message img {
border: 1px solid #3e3e3e !important;
}

#Form_Search {
width: 15em !important
}

.StatBox {
font-size: 80% !important;
}

.Activities {
border-top: 1px solid #3e3e3e !important;
margin-top: 10px !important;
}

.FirstComment {
border-bottom: 1px solid #3e3e3e !important;
}

div#Content li.Item {
border-left: 1px solid #3e3e3e !important;
border-right: 1px solid #3e3e3e !important;
}

div#Content .Tabs li {
border: none !important;
}

.Tabs *,
.BigButton {
border-radius: 0px !important;
}

#Panel li {
border-left: 1px solid #3e3e3e !important;
border-right: 1px solid #3e3e3e !important;
}

span.Votes span {
padding: 0px !important;
}

.VoteUp {
width: 0px !important;
height: 0px !important;
padding: 0px !important;
margin-left: 20px !important;
border: 8px solid !important;
border-color: transparent transparent #556b2f transparent !important;
}

.VoteDown {
width: 0px !important;
height: 0px !important;
padding: 0px !important;
border: 8px solid !important;
border-color: #556b2f transparent transparent transparent !important;
}

#Menu,
.Menu {
padding-top: 20px !important;
}

.LastCommentDate,
.MetaItem {
padding-left: 1em !important;
padding-right: 1em !important;
}

.ArrowSprite {
display: none !important;
}

.DataList .Meta Strong {
background: #262626 !important;
position: absolute !important;
left: -50px !important;
top: 10px !important;
}

.Meta .Announcement {
position: absolute !important;
left: 560px !important;
bottom: 0px !important;
}

.Meta .DiscussionCount {
padding-right: 10px !important;
float: right !important;
}

.Meta .Closed {
position: absolute !important;
left: 500px !important;
bottom: 0px !important;
}

.Meta .Category {
position: absolute !important;
left: 260px !important;
}

.Discussions .Meta .LastCommentDate {
position: absolute !important;
left: 160px !important;
}

.CategoryList .Meta .LastCommentDate {
position: absolute !important;
left: 470px !important;
}

.Votes {
position: absolute !important;
left: 620px !important;
top: -10px !important;
}

.Votes * {
font-size: small !important;
}

.MessageForm {
border: 1px solid #3e3e3e !important;
margin-top: 30px !important;
}

#quoteButton {
display: none !important;
}

.Options {
position: absolute !important;
left: 624px !important;
}

.Options * {
visibility: visible !important;
padding: 3px !important;
background: #262626 !important;
}

.Discussions .Options {
display: none !important;
}

.Activities .Photo {
padding-left: 4px !important;
}

.Conversation .Photo {
padding-top: 5px !important;
}


/**************** Images *******************/


img[alt="Spiralinear"] {
display: none !important;
}

img[src*="rss.gif"] {
width: 0px !important;
height: 14px !important;
background: #af5f00 !important;
background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAFo9M/3AAAABGdBTUEAALGPC/xhBQAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAFNJREFUOMvFktEWABAIQ1v//8/z6lApHO6bhTYRqUF3EW9XS6TswsgFO3G/xR3oeWDmHc0k2glIz+otSFhB5YJVPlQdwKjhZKpTRDUOjJ1+f88FDdC2HfJaHzIRAAAAAElFTkSuQmCC) !important;
padding-left: 14px !important;
}


}
