css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.a9vg.com") {

  /* 去头尾 */
  #hd, #ft, #a9_show { display: none; }

}

@-moz-document url-prefix("http://bbs.a9vg.com/thread") {

  /* 个人信息, 悬停显示 */
  .pls > .cl { display: none; }
  .pls:hover > .cl { position: absolute; display: block; width: 130px; background-color: #E5EDF2; }

  /* 隐藏回复引用按钮, 需要的把下面这行注释掉 */
  .plc > .po { display: none; }
  
  /* 隐藏收藏按钮 */
  .plc > .uo { display: none; }

  /* 加大贴子字号 */
  .t_f { font-size: 18px !important; }

  /* 签名中限制只有一个换行, 影响了版式的把下面这行注释掉 */
  .sign > br + br { display: none !important; }

}
