css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(facebook.com)
{
/* Main */
#leftCol, #rightCol, #pageLogo, #navSearch, #pagelet_ads, #left_column, #pageFooter, .UIStandardFrame_SidebarAds, #gb_muffin_area, .GBSearchBox, .UIButtonStrip, .UIToolbarWell_Right, #headerArea, #pagelet_photo_bar, #pageNav, #pagelet_presence, .uiMorePagerAnchor .secondary, #ConfirmBannerOuterContainer, #MessagingNetegoWrapper, #pagelet_roosters, .attachmentSelectForm
{
	display: none !important;
}
#globalContainer, #contentCol, #contentArea, #headNav, .right_column_container, #profile_top_bar, #pageHead, #tab_canvas, .uiUfi, .UIStandardFrame_Container, .UIStandardFrame_Content, #gb_content_and_toolbar, .right_column, .uiHeaderImage, #blueBarHolder, #blueBar
{
	padding: 0 !important;
	margin: 0 !important;
	width: auto !important;
	height: auto !important;
	max-width: none !important;
	min-width: 0 !important;
}
#pageHead
{
	position: fixed !important;
	right: 0 !important;
	z-index: 100 !important;
}
.fbJewelFlyout
{
	left: -248px !important;
}

/* Messaging */
#contentArea, #MessagingMainContent, #MessagingFrame
{
	width: 100% !important;
}
#MessagingScroller
{
	margin-bottom: 10px !important;
}
#MessagingShelf
{
	position: relative !important;
	width: 100% !important;
}
.MessagingComposerBody
{
	width: 100% !important;
	height: 10em !important;
	resize: both !important;
}
}
