css:
@-moz-document domain('wikia.com') {
	p {
		font-size: 1.25em !important;
		line-height: 1.75em;
		font-family: Palatino, Georgia, serif;
	}

	#WikiaArticle {
		padding: 10px 50px !important;
	}

	#WikiaPageHeader details {
		width: 100% !important;
	}

	.likes {
		display: none;
	}

	#TOP_LEADERBOARD {
		display: none;
	}

	#WikiaRail {
		display: none;
	}

	#WikiaMainContent {
		width: 100% !important;
	}

	#WikiaFooter {
		display: none !important;
	}
}
