css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("todoist.com") {

HTML {
    border-top: 2px solid #0099DD !important;
    }

BODY {
    background: url("data:image/png;base64,")
    repeat-x !important;
    }

body, textarea, input, select, option {
    font-family: 'Lucida Sans Unicode', Tahoma, Verdana !important;
    font-size: 12px !important;
    }

.actions, h2 {
    font-size: 12px !important;
    }

.items li{
    border-bottom: 1px solid #0099DD !important ;
    }

.items li, .text_box, td.name {
    font-size: 12px !important;
    }

.items .current {
    background-color: #ffffcc !important;
    font-style: italic !important;
    color: red !important;
    }

.manager .text_box {
    font-size: 12px !important;
    }

.date {
    font-size: 10px !important;
    height: 15px !important;
    display: block !important;
    }

.div_counter {
    font-size: 10px !important;
    font-weight: bold !important;
    }

.label {
    font-size: 10px !important;
    }

.day_count, .h2_date, #project_list .separator, .label_big {
    font-size: 12px !important;
    }

.auto_exapnd_ta, .auto_exapnd_clone {
    font-size: 12px !important;
    font-family: verdana !important;
    }

#agenda_box, #agenda_view li, #agenda_view .project, #agenda_view .time {
    font-size: 12px !important;
    vertical-align: top !important;
   }

td.name, td.text_cursor, td.content {
    vertical-align: top !important;
    }

#top {
    border-bottom: 1px solid #0099DD !important;
    }

}
