css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.insessiontrials.com") {

/* change pointer for focused items */
input.textbox, input[type="radio"],.editor_textbox textarea, ul.navbar_advanced_search li a,input[type="checkbox"], select, ul[class="checkradio group rightcol"] > li > label, img.imagebutton, .popupmenu {
cursor:pointer !important;
}

/* text box when has focus */
.formcontrols .blockrow input.textbox:focus, .formcontrols .blockrow textarea:focus, .globalsearch input.textbox:focus  {
background : none repeat scroll 0 0 #FCFAE3 !important;
}

/* size of text in top right for the links */
div[class="toplinks"] {
color:#FFFFFF !important;
font-size:14px !important;
line-height:1.6 !important;
text-align:right !important;
}

/* size of text in top right for the links - notifications */
li[id="notifications"] > a {
color: #FFF700 !important;
font-size:14px !important;
line-height:1.6 !important;
text-align:right !important;
}

/* remove gray side bars */
body {
font:10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
margin:5px 0 10px !important;
padding:0 !important;
}

/* This is for the main toolbar */

/* makes advanced search link show */
div[id="navbar"][class="navbar"]{
height:60px !important;
}

.navtabs li.selected li a, .navbar_advanced_search li a {
font:13px/26px Arial,Calibri,Verdana,Geneva,sans-serif !important;
text-decoration:none;
}

/* moves forum button to right */
ul[id="navtabs"][class="navtabs floatcontainer"]{
padding:0 0 0 0 !important;
}

span[class="buttoncontainer"]{
height:27px  !important;
display:block !important;
float: none !important;
}

/* reposition magnifing glass after textbox */
input[class="searchbutton"]{
margin-top:-2px !important;
}

/* font for thread tools */
ul[id="postlist_popups"] > li > h6 > a {
font-size:14px !important;
line-height:1.23 !important;
}

/************************************************/

/* page numbers on bottom\top right - blue*/
form[class="pagination popupmenu nohovermenu"] > span > a {
background-color: #E1E4F2 !important;
color: black !important;
font:13px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
border: 1px solid #8283AB !important;
}

/* page numbers on bottom\top right - yellow (selected) */
form[class="pagination popupmenu nohovermenu"] > span[class="selected"] > a {
background-color: #FFFC99 !important;
}

/* page numbers on bottom\top right - white */
form[class="pagination popupmenu nohovermenu"] > span > a.popupctrl {
background-color: white!important;
}

/* This is the space after user post */
blockquote[class="postcontent restore"] {
padding-bottom:25px !important;
}

/* This is the last edit field */
blockquote[class="postcontent lastedited"] {
color: #A89E9E !important;
font-weight:normal  !important;
padding-top:1px !important;
padding-bottom:1px !important;
text-align:left !important;
}

/* This is the last edit field also */
blockquote[class="postcontent lastedited"] > a {
color: #A89E9E !important;
font-weight:normal  !important;
}

/* This is the last edit field span section */
blockquote[class="postcontent lastedited"] > span {
color: #A89E9E !important;
font-weight:normal  !important;
}

/* This is the quote inside the quote */
.bbcode_container div.bbcode_quote {
background: #E1E4F2 !important;
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}

/* This is the post date bar */
div[class="posthead"] {
background-color: #7192A8 !important;
}

/* This is the user quote */
div[class="postrow"] {
background-color: #F5F5FF !important;
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
padding:5px 10px 1em !important;
}

/* This is the user quote */
div[class="postbody"] {
}

/* This is the user quote title icons */
div[class="postrow"] > h2 > img {
vertical-align: middle !important;
}

/* This is the user quote title */
div[class="postrow"] > h2 {
color: #284C63 !important;
background-color: #F0F0FC!important;
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
font-weight:bold !important;
border:1px solid #E9E9E9 !important;
padding:5px !important;
text-align: left !important;
margin-right: 5px !important;
margin-left: 5px !important;
padding-left:35px !important;
border-bottom:4px solid #DDDDDD !important;
}

/* This is the user quote space between title and content */
div[class="postrow"] > div[class="content"]{
padding:6px !important;
}

/* This is the little left right white left over */
div[class="body_wrapper"] {
background-color: #FFFFFF !important;
}

/* This is the background square username is in */
div[class="userinfo_noavatar"] {
background-color: #E1E4F2 !important;
}

/* This is the background square username is in */
div[class="userinfo_avatar"] {
background-color: #E1E4F2 !important;
}

/* This is the username that posted */
a[class="username offline popupctrl"] {
font-size:1.4em !important;
}

/* This is the username that posted */
a[class="username online popupctrl"] {
font-size:1.4em !important;
}

/* This is the username that posted */
a[class="username invisible popupctrl"] {
font-size:1.4em !important;
}
/* This is the signature */
div[class="signaturecontainer"] {
color: #A89E9E !important;

}

/* This is the signature */
blockquote[class="signature restore"] {
margin-top:0em !important;
padding-top:.5em !important;
border-top:3px solid #DDDDDD !important;
}

/* This is the user title under username */
span[class="usertitle"] {
color: #7D7474 !important;
font:10px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
}

/* This is the reply button */
a[class="newreply"] {
color: #365480 !important;
font:18px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
font-style:italic !important;
padding-right:20px !important;
}

/* This is the reply button : hover */
a.newreply:hover {
color: #3ED667 !important;
font:18px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
font-style:italic !important;
padding-right:22px !important;
}

/* This is the Edit button */
a[class="editpost"] {
color: #365480 !important;
font:18px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
font-style:italic !important;
padding-right:20px !important;
}

/* This is the Edit button : hover */
a.editpost:hover {
color: #3ED667 !important;
font:18px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
font-weight:bold !important;
font-style:italic !important;
padding-right:22px !important;
}


div[id="posting_rules"]  {
background-color: #F5F5FF !important;
}

div[id="forumrules"]  {
background-color: #F5F5FF !important;
}

div[id="forum_icon_legend"]  {
background-color: #F5F5FF !important;
}

/************************************************/

/* below is new post page */

/* This is the little left right white left over */
div[class="body_wrapper"] {
background-color: #FAFAFA !important;
}

/* title text box */
input[class="primary textbox full"], input[class="primary full textbox"]{
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
height:30px !important;
}

/* title text box - reason for editing textbox */
input[class="primary textbox"]{
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}

/* reason for deleting text box */
input[class="textbox primary"]{
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
height:30px !important;
}


/* the post list */
ul[id="postlist"] > li {
background-color: #F5F5FF !important;
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}

div[class="blockfoot actionbuttons"] {
background-color: #FAFAFA !important;
}

div[class="blockbody formcontrols"] {
background-color: #F5F5FF !important;
}

div[class="blockrow texteditor"] {
background-color: #F5F5FF !important;
}


/* smiles to the right of edit text box */
.editor_smiliebox ul.smiliebox li {
    float: left !important;
    height: 27px !important;
    margin: 2px !important;
    overflow: hidden !important;
    position: relative !important;
    text-align: center !important;
    width: 20px !important;
}

.editor_smiliebox ul.smiliebox {
    height: 245px !important;
    overflow: hidden !important;
}

/*****************************************/


/* edit text box */
div[class="editor_textbox"] > textarea {
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}

/* reply text box */
div[class="editor_textbox editor_textbox_smilie"] > fieldset > textarea {
font:11pt/14pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}

div[class="blockrow posticons"] {
background-color: #F5F5FF !important;
}

div[class="blockbody formcontrols"] > div > div {
background-color: #F5F5FF !important;
}

div[class="blockbody formcontrols"] > div > fieldset {
background-color: #F5F5FF !important;
}

div[class="texteditor quickedit formcontrols"] > div  {
background-color: #F5F5FF !important;
}

/* sides edit */
div[class="body_wrapper"] > form[class="block vbform"] {
padding:20px 20px !important;
max-width: none !important;
}

/************************************************/

/* below is forums pages */

/* title text unread */
li > div > div > div > h3 > a {
color: black !important;
font-size:14px !important;
font-weight:normal !important;
}

/* title text read */
li.new > div > div > div > h3 > a {
color: black !important;
font-size:14px !important;
font-weight:bold !important;
}

/* This is threads that are locked */
li.lock > div.rating0 {
background-color: #FFF5F5 !important;
}

/* This is threads that have no unread */
.rating0 {
background-color: #F7F7F7 !important;
}

/* This is threads that have unread */
li.new > div.rating0 {
background-color: #E6E7FF !important;
}

/* This is the stickys without new posts */
li.threadbit  > div.sticky {
background-color: #E6E6E6 !important;
}

/* This is the sticky with new posts */
li.new > div.sticky {
background-color: #E6E6E6 !important;
}

/* This is the page # buttons */
dl[class="pagination"]  {
vertical-align: middle !important;
}

/* This is the page # buttons */
div[id="jump-to-folder"] > div {
margin:0 5px !important;
}

/* smilies */
img.inlineimg { 
vertical-align: top !important; 
overflow: visible !important; 
}

/* sides */
div[class="body_wrapper"]{
padding:20px 20px !important;
}



}
