css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.norshroom.org/ubbthreads/) {
/* File Version 6.5.2b1 */

a:link  {
  color: #000000 !important;
  background: none !important;
}
a:visited {
  color: #000000 !important;
  background: none !important;
}
a:active {
  color: #000000 !important;
  background: none !important;
}
a:hover {
	color: #B8BDD1 !important;
	background: none !important;
	text-decoration: none !important;
}
p,table,td,tr {
  font-size: 10pt !important;
  font-family: Verdana, Arial, Helvetica, sans-serif !important;
}
form {
  display: inline !important;
  margin-bottom: 0 !important;
}
body {
	color: #000000 !important;
	background: #E5E7EB !important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
    font-size: 10pt !important;
}
.tablesurround {
	background: #D6DBDF !important;
	color: #000000 !important;
}
.tableborders {
	background: #E5E7EB !important;
	color: #000000 !important;
}
.tdheader {
  font-size: 10pt !important;
  font-weight: bold !important;
  color: #000000 !important;
  background: #4F6572 !important;
   border-top:1px solid #FFF !important;
}
.menubar{
	background: #D6DBDF !important;
	color: #000000 !important;
   font-size: 9pt !important;
}
.navigation{
	background: #4F6572 !important;
	color: #000000 !important;
	font-size: 10pt !important;
	padding: 1px !important;
	margin: 1px !important;
	font-weight: bold !important;
	border: 1px solid #4F6572 !important;
}
.catandforum {
  font-size: 8pt !important;
}
.posttime {
	font-size: 10pt !important;
	background: #E5E7EB !important;
	color: #000000 !important;
}
.threadtotal {
	font-size: 10pt !important;
	background: #E5E7EB !important;
	color: #000000 !important;
}
.posttotal {
  font-size: 10pt !important;
  background: #E5E7EB !important;
  color: #000000 !important;
}
.modline {
  font-size: 8pt !important;
  font-style: oblique !important;
}
.small {
  font-weight: normal !important;
  font-size: 8pt !important;
}
.standouttext {
  color: #ff0000 !important;
  background: none !important;
}
.pollcolor {
	background: #ff0000 !important;
	color: #000000 !important;
	border: 1px solid #4F6572 !important;
}
.forumtitle {
  font-size: 10pt !important;
  font-weight: bold !important;
}
.forumdescript {
  font-size: 8pt !important;
}
.lighttable {
  background: #E5E7EB !important;
  color: #000000 !important;
}
.darktable {
  background: #D6DBDF !important;
  color: #000000 !important;
}
.newlighttable {
  background: #E5E7EB !important;
  color: #000000 !important;
}
.newdarktable {
  background: #A9BFD1 !important;
  color: #000000 !important;
}
.newpostsincat {
	background: #FFFF00 !important;
	color: #000000 !important;
}
.cleartable {
	background: #f7f7f7 !important;
	color: #000000 !important;
}
.newsubjecttable {
	background: #A9BFD1 !important;
	font-weight: bold !important;
	color: #000000 !important;
}
.subjecttable {
  background: #A9BFD1 !important;
  color: #000000 !important;
}
.formboxes{
  background-color: #ffffff !important;
  color: #000000 !important;
  font-size: 10pt !important;
}
.buttons {
	background-color: #4F6572 !important;
	color: #FFFFFF !important;
	font-size: 9pt !important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	border-top-width: 2px !important;
	border-right-width: 2px !important;
	border-bottom-width: 2px !important;
	border-left-width: 2px !important;
	border-top-style: solid !important;
	border-right-style: solid !important;
	border-bottom-style: solid !important;
	border-left-style: solid !important;
	border-top-color: #4F6572 !important;
	border-right-color: #4F6572 !important;
	border-bottom-color: #4F6572 !important;
	border-left-color: #4F6572 !important;
	font-weight: bold !important;
}
blockquote {
   font-size: 9pt !important; border: 1px solid #4F6572 !important;
   margin: 0px 20px !important; padding: 0px 10px !important
}
blockquote .small {
	background: #4F6572 !important;
	margin-left:-10px !important;
	padding: 1px 2px !important;
	color: #FFFFFF !important;
}
blockquote hr {
   visibility:hidden !important;
   height: 1px !important
}
blockquote br {
   font-size: 1px !important
}
.post:first-letter {
	font-weight: bold !important;
}
pre {
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 10pt !important;
	display: block !important;
	white-space: pre !important;
}
