.newlighttable a:visited {
}
.newlighttable a:active {
}
.newlighttable a:hover {
}

.newdarktable a:link  {
}
.newdarktable a:visited {
}
.newdarktable a:active {
}
.newdarktable a:hover {
}
.modname {
	    color: #00AA00 !important;
}
.adminname {
	    color: #FF0000 !important;
}


}
