css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{
    /* Friend suggestions and other crap */
    div#pagelet_netego,
    div#netego_organic,
    div.ego_column,
    /* Friends left column */
    div.relationshipSection,
    /* Right column on friend list page */
    div#pagelet_friendlist_sidecol,
    /* Advertisements */
    div#pagelet_netego_ads,
    div#pagelet_ads,
    div#pagelet_adbox,
    div#sidebar_ads,
    /* New ticker on the right */
    div.tickerActivityStories
    {
        display: none;
    }
}
