/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml); @-moz-document domain("4chan.org") {
/* CloudFlare Fix: */ html,body {width: auto !important; height: auto !important;} ul#Alerts li.alert {background: white !important;}
body {font-size: 1em !important; margin: 25px 8px 8px !important;} body>img[style*="moz-zoom-in"] {max-width: 100% !important; height: auto !important;}
html {background: white repeat-x !important; background-image: url("https://lh5.googleusercontent.com/-9mPfq_bTK4o/T4qBmWVF8eI/AAAAAAAAAMQ/ZojfswVTVrQ/I/backgrnd777.jpg"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAVCAYAAABsZT8HAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAANsAAADbABfWVZ+gAAAAd0SU1FB9oGAxEqHfMNphEAAAA5SURBVAjXY2SQUvnPwMDAwMLAyMQAYTAwQhmMDAwMDIzIUoyMOBkMmFIk6SJCMYxRH+rKwMDAwAAAGzcCz5PMGZsAAAAASUVORK5CYII=") !important; background-position: center 21px,0 0 !important; /*overflow-y: scroll !important;*/ overflow: visible !important;}
body,.content-background {background: transparent !important; color: black !important;}

a {color: #034 !important;}
h2,.reply,.pagelist,ul,#absbot {color: black !important;}
.postertrip {color: #567 !important;}
.fileInfo,.summary,.p_filesize {color: #666 !important; font-size: 0.8em !important;}
blockquote,td,table,tr {font-size: 1em !important;}
.quote {color: #539 !important;}
.filetitle,.p_filetitle {color:#002244 !important; font-size:18px !important;}
.name,.p_postername {color:#004A99 !important;}
.postNum {font-size: 0.8em !important; font-weight: bold !important;}
.postNum a {color: black !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;}
.backlink {font-size: 0.8em !important;}
h2 {background: transparent !important;}

.globalMessage {font-size: 0.8em !important; color: black !important; background: rgba(255,255,255,0.4) !important; border: 1px solid #A6A697 !important; border-radius: 5px !important; padding: 5px !important;}
.globalMessage strong,.globalMessage big {font-size: inherit !important;}
.globalMessage strong {font-weight: normal !important;}

.thread {clear: none !important;}

a.extButton img {display: none !important;}
a.extButton {margin: 0 2px 0 3px !important; font-weight: bold !important; color: gray !important;}
a.extButton:after {content: ">>" !important;}

.reply,#delform .reply,#preview-tooltip-ws {border: 1px solid #ccc !important;}
.reply,#delform .reply,#preview-tooltip-ws {background: none #eee !important; border-radius: 5px !important;}

hr {color: transparent !important; border: solid black !important; border-width: 1px 0 0 !important; opacity: 0.15 !important; z-index: -1 !important;}

#logo,.sideArrows,.title,.boardSubtitle,body>hr,form>hr {display: none !important;}

.fileThumb img,#preview-tooltip-ws img {float: left !important; margin: 2px 20px !important;}
.fileThumb {margin: 0 !important;}
blockquote {margin: 5px 5px 0 !important;}

input,textarea,select,button,.postForm td:first-child {-moz-appearance: none !important; color: black !important; font-size: 12px !important; border: 1px solid #ccc !important; background: #eee !important;}
select input {border: 0 none !important;}
input[type="checkbox"] {border: 1px solid #A6A697 !important; background-color: white !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}
input[type="file"] div {margin: 0 !important;}
input[type="button"],input[type="submit"] {margin:1px !important;}
textarea {min-height: 25px !important; min-width:326px !important;}

/*.qrHeader {display: none !important;}*/
.qrForm {padding: 0 !important; margin: 2px 0 0 !important;}
.qrButtonHolder {display: block !important; right: auto !important; top: auto !important; float: right !important; position: static !important;}
.qrButtonHolder img:before {content: attr(title) !important;}
.qrButtonHolder img {margin: 0 5px 0 0 !important;}
.qrWindow {border: 0 none !important; border-radius: 0 !important; background: none transparent !important;}
.postblock {text-align: left !important; display: block !important; padding: 5px 20px !important; margin: 0 !important; border: 1px solid #A6A697 !important; border-radius: 5px !important;}
.qrWindow .postForm,.postblock {background: rgba(255,255,255,0.8) !important;}

form[name="post"] {text-align: center !important;}
form .postForm {text-align: left !important; display: inline-block !important; padding: 10px 20px !important; margin: 0 0 15px !important; background: rgba(255,255,255,0.4) !important; border: 1px solid #A6A697 !important; border-radius: 5px !important;}
#recaptcha_table td {padding: 0 !important; border: 0 none !important; background: transparent !important;}
#recaptcha_table td:last-child {display: none !important;}
#recaptcha_image {margin: 0 2px 0 0 !important;}
.recaptcha_input_area {padding: 0 !important;}

.pagelist {background: none transparent !important; border: 0 none !important; float: left !important;}

img,td,#ft ul,#ft li.current, #ft li.fill,#ft li {border: 0 none !important;}

#boardNavDesktop,.boardSelect,.postingMode {display: none !important;}
#boardNavMobile {display: block !important; left: auto !important; top: auto !important; overflow: visible !important; width: auto !important; position: static !important; padding: 0 !important; border: 0 none !important;}

.boardTitle {position: absolute !important; display: block !important; left: 0; top: 0; width: auto !important; letter-spacing: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; line-height: 21px !important; padding: 0 8px !important; text-decoration: none !important; font-style: normal !important;}

.pageJump {position: absolute !important; width: 200px !important; display: block !important; left: 50% !important; right: auto !important; top: 0 !important; margin: 0 0 0 -100px !important; padding: 0 !important; float: none !important; text-align: center !important;}
.pageJump,.pageJump a {color: #ccc !important;  line-height: 21px !important; text-decoration: none !important; font-style: normal !important;  letter-spacing: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;} #navtopr a:hover {color: white !important;}
.pageJump a {font-weight: bold !important; padding: 0 8px !important;}

body>.navLinks {position: absolute !important; display: block !important; left: auto !important; right: 0 !important; top: 0 !important; margin: 0 !important; float: none !important;}
body>.navLinks,body>.navLinks a {color: #ccc !important; padding: 0 8px !important; line-height: 21px !important; text-decoration: none !important; font-style: normal !important;  letter-spacing: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;} #navtopr a:hover {color: white !important;}
body>.navLinks a {font-weight: bold !important;}
}

@-moz-document url-prefix("http://www.4chan.org/framesnav"),url-prefix("https://www.4chan.org/framesnav") {
body {font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; font-size: 13px !important;}
div[style*="none"] {display: block !important; min-height: 21px !important;} div[style*="none"] *,ul h2 {display: none !important;}
h2 {position: absolute !important; width: auto !important; border: 0 none !important; right: 8px !important; left: 8px !important; margin: 2px 0 0 !important; padding: 2px 4px !important;}
h2 span {position: absolute !important; right: 4px !important; border: 1px solid gray !important; background: #eee !important; width: 13px !important; height: 13px !important; text-align: center !important; padding: 0 !important; border-radius: 1em !important; border-radius: 1em !important; font-size: 12px !important;}
li,h1 {margin: 0 !important; border: 0 none !important; padding: 0 !important;}
ul+ul,ul ul {padding: 2px 4px !important;}
#logo+ul {padding: 2px 4px 4px !important;}
li {background: transparent !important;} ul {list-style: none !important;}
a {text-decoration: none !important; line-height: 100% !important;}
a[href$="/b/"] {font-weight: bold !important;}
a:hover {text-decoration: underline !important;}
ul {border: 1px solid #A6A697 !important; background: rgba(255,255,255,0.4) !important; margin: 2px 0 !important; padding: 19px 4px 2px 4px !important; border-radius: 5px !important; border-radius: 5px !important; -webkit-border-radius: 5px !important;}
}
