/* once again, I take no credit for this. this was all done by the awesome @chrismasterson. :) */ 

@-moz-document domain('twitter.com')

body {
	background-position: 0% 0px !important;
	-webkit-font-smoothing: subpixel-antialiased !important;
}

* {
	font-family: 'Helvetica Neue', sans-serif !important;
}

#page-container {
	-webkit-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.3) !important;
	max-width: 840px !important;
}

#top-stuff {
	margin-top: 40px !important;
	position: static !important;
	z-index: 1000;
}

.latest-favorite, .user-rec-inner, .favorites-activity, #top-bar-bg, .recently-listed-in, .profile-links,#new-tweet, .vr, .tweet-box-title, .trends-inner, .component .trends-inner + hr, .trends-inner, .component .user-rec-inner + hr, .related-tweets, .mentioned-in-tweet, .recent-tweets {
	display: none !important;
}

#search-form {
	display: none !important;
}

#search-query {
	display: none !important;
}
#top-bar {
	margin: 15px auto !important;
	max-width: 1000px !important;
}

#top-bar-outer {
	height: none !important;
}

#page-outer {
	padding-top: 15px !important;
	max-width: 1000px !important;
}

#logo a {
	background: transparent url(data:image/png;base64,) no-repeat 18px 9px !important;
}

#session {
	padding: 10px 24px 10px 25px !important;
}

#session {
	background: none !important;
}

.profile-dropdown {
	z-index: 999 !important;
}

#top-bar ul.profile-dropdown {
	-webkit-box-shadow: rgba(0, 0, 0, 0.597656) 0px 1px 2px !important;
}


.profile-dropdown li a {
	background: white !important;
	color: gray !important;
}

.profile-dropdown li a:hover {
	background: white !important;
	color: black !important;
}

.your-activity {
	height: 25px !important;
	margin-bottom: 5px !important;
}

.main-content {
	-webkit-box-shadow: #999 0px 0px 4px !important;
	border-top-left-radius: 10px 10px !important;
}

.static-links.active {
	background: transparent !important;
	text-shadow: rgba(0, 0, 0, 0.8) 0px 1px 2px !important;
}

.static-links .active a {
	background: transparent !important;

}

#global-nav {
	margin-top: 1px !important;
	text-shadow: rgba(0, 0, 0, 0.59375) 0px 1px 2px !important;
	color: white !important;
}

#global-nav a {
	color: white !important;
}

#screen-name {
	color: white !important;
	text-shadow: rgba(0, 0, 0, 0.4) 0px 1px 2px !important;
}

.main-tweet-box {
	padding: 20px 0px !important;
}

.tweet { 
	font-size: 14px !important;
}

.stream-tab.active, .stream-tab.active {
	background: white; !important;
}


.details-pane-shell {
	max-width: 920px !important;
}

.details-pane-outer {
	top: 95px !important;
	
}

.details-pane {
	-webkit-box-shadow: rgba(0, 0, 0, 0.296875) 0px 2px 4px !important;
	height: auto !important;
	max-height: 650px !important;
	overflow-y: inherit !important;
}

#message-drawer {
	top: 56px !important;
}

.pane-components {
	height: auto !important;
	overflow-y: hidden !important;
}

.pane-toolbar {
	background: #f9f9f9 !important;
}

.twttr-dialog {
	background-color: white !important;
	-webkit-box-shadow: rgba(0, 0, 0, 0.5) 0px 1px 10px !important;
	top: 150px !important;
}

.twttr-dialog-inside {
	-webkit-box-shadow: none !important;
}

.twttr-dialog-close {
	top: 0px !important;
	line-height: 16px !important;
}
h3 {
	color: gray !important;
}

.gray-component {
	background: transparent !important;
}

.profile-actions {
	margin-bottom: 15px !important;
}

.title-link {
	font-size: 14px;
}


.your-activity {
	width: auto !important;
	float: none !important;
}

div.latest-tweet .tweet-row {
	height: auto !important;
}
div.latest-tweet .tweet-image, div.latest-tweet .tweet-image img {
	display: none !important;
}

div.latest-tweet .tweet-content {
	margin-left: 0px !important;
	min-height: 0px !important;
}

.new-tweets-bar {
	background: #f7f7f7 !important;
}

::-webkit-scrollbar {
	width: 5px !important;
}

::-webkit-scrollbar-button:start:decrement,
::-webkit-scrollbar-button:end:increment {
    display: none !important;
}


::-webkit-scrollbar-track-piece {
    background-color: #d5d5d5 !important;
    -webkit-border-radius: 5px !important;
    width: 3px !important;
}

::-webkit-scrollbar-thumb:vertical {
    height: 50px !important;
    background-color: #f7f7f7 !important;
    -webkit-border-radius: 5px !important;
    width: 3px !important;
}

::-webkit-scrollbar-thumb:horizontal {
    width: 50px !important;
    background-color: #f7f7f7 !important;
    -webkit-border-radius: 5px !important;
    width: 3px !important;
}

.buttons .drop-down {
    top: -120px !important;
}
