css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
  domain("kotaku.com.au"),
  domain("gizmodo.com.au"),
  domain("lifehacker.com.au")
{

BODY {
font: 12px "Helvetica", "Calibri" !important;
color: #EEE !important;
background: #222 !important;
}

#network-nav,
#ad-leaderboard-top,
#allure-pushdown,
#undermast,
#ad-leaderboard-bottom,
#footer {
display: none !important;
}

#header {
margin-top: 10px !important;
}

#header > #navigation {
display: none !important;
}

#header > #search INPUT {
-moz-appearance: none !important;
-webkit-appearance: none !important;
border: 1px solid #222 !important;
padding: 3px !important;
background: #FFF !important;
-moz-border-radius: 3px !important;
font: 10px "Helvetica", "Calibri" !important;
color: #222 !important;
}

#body {
border: 0px !important;
padding: 0px !important;
background: #222 !important;
}

#content {
border: 0px !important;
float: none !important;
height: 100% !important;
padding: 0px !important;
width: auto !important;
background: #222 !important;
}

#quick-links,
#sidebar,
.comments,
.comments-form {
display: none !important;
}

DIV.excerpt {
padding: 5px !important;
margin: 0px !important;
border-bottom: 1px solid #555 !important;
}

DIV.excerpt > A.category {
padding: 0px !important;
margin: 0px !important;
text-transform: lowercase !important;
background: none !important;
font: 12px "Helvetica", "Calibri" !important;
color: #EEE !important; 
text-shadow: 0px 0px 5px #000 !important;
width: 100px !important;
position: absolute !important;
top: 15px !important;
left: 10px !important;
z-index: 2 !important;
}

A.thumbnail {
min-width: auto !important;
width: auto !important;
max-width: auto !important;
z-index: 1 !important;
}

A.thumbnail > IMG {
min-width: auto !important;
width: auto !important;
max-width: auto !important;
float: left !important;
margin: 5px 5px 5px 0px !important;
padding: 0px !important;
z-index: 1 !important;
border: 1px solid #AAA !important;
}

A.thumbnail:focus {
outline: 0px !important;
}

DIV.post > DIV.btyb,
DIV.post > A.category {
display: none !important;
}

DIV.post > H1 {
font: 20px "Helvetica", "Calibri" !important;
color: #EEE !important;
font-weight: bold !important;
text-shadow: 0px 1px 0px #000 !important;
text-align: center !important;
margin: 0px !important;
}

DIV.subtitle {
margin: 0px !important;
text-align: center !important;
}

DIV.subtitle > DIV.share {
display: none !important;
}

DIV.subtitle > DIV.byline {
padding: 0px !important;
font: 12px "Helvetica", "Calibri" !important;
color: #AAA !important;
line-height: 15px !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.subtitle > DIV.byline > A {
color: #EEE !important;
}

DIV.subtitle > DIV.byline > A:hover {
color: #AAA !important;
}

H2 {
text-align: center !important;
margin: 0px !important;
width: 100% !important;
clear: none !important;
}

H2 > A {
font: 14px "Helvetica", "Calibri" !important;
color: #EEE !important;
font-weight: bold !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.lead > A.thumbnail + SPAN,
DIV.lead > H2 + SPAN {
font: 12px "Helvetica", "Calibri" !important;
color: #AAA !important;
text-transform: none !important;
line-height: 15px !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.lead {
font: 12px "Helvetica", "Calibri" !important;
line-height: 15px !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.copy {
font: 12px "Helvetica", "Calibri" !important;
line-height: 15px !important;
text-shadow: 0px 1px 0px #000 !important;
}

BLOCKQUOTE {
width: 90% !important;
padding: 10px !important;
margin: 10px auto !important;
background: #333 !important;
border: 1px solid #AAA !important;
}

BLOCKQUOTE > P {
margin: 0px auto !important;
}

DIV.copy > P > A,
DIV.lead > A {
color: #999 !important;
}

DIV.copy > P > A:hover,
DIV.lead > A:hover {
color: #AAA !important;
}

A.more-link {
display: none !important;
}

DIV.meta {
padding: 0px !important;
margin: 0px !important;
}

DIV.meta > DIV.links,
DIV.tags > DIV.btyb,
DIV.tags > DIV.share {
display: none !important;
}

DIV.copy + DIV.tags {
display: none !important;
}

DIV.tags {
width: 100% !important;
}

DIV.tags > STRONG {
font: 12px "Helvetica", "Calibri" !important;
color: #555 !important;
text-shadow: 0px 1px 0px #000 !important;
margin-right: 5px !important;
}

DIV.tags > H2 > A {
font: 12px "Helvetica", "Calibri" !important;
color: #999 !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.meta A {
color: #999 !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV.tags > H2 > A:hover,
DIV.meta A:hover {
color: #AAA !important;
}

DIV.date-change {
border-bottom: 1px solid #555 !important;
padding: 0px !important;
margin: 0px !important;
background: #222 !important;
text-align: center !important;
}

DIV.date-change > A {
font: 16px "Helvetica", "Calibri" !important;
color: #AAA !important;
text-shadow: 0px 1px 0px #000 !important;
}

#post-pagination {
border: 0px !important;
margin: 0px !important;
padding: 0px !important;
clear: both !important;
}

LI.next {
background: none !important;
width: 40% !important;
float: right !important;
text-align: right !important;
margin: 5px !important;
}

LI.next > A {
font: 14px "Helvetica", "Calibri" !important;
color: #999 !important;
text-shadow: 0px 1px 0px #000 !important;
width: 100% !important;
float: right !important;
border: 0px !important;
padding: 0px !important;
text-align: right !important;
}

LI.next > A:hover,
LI.previous > A:hover {
color: #AAA !important;
background: none !important;
}

LI.next > A:focus,
LI.previous > A:focus {
outline: 0px !important;
}

LI.previous {
background: none !important;
width: 40% !important;
float: left !important;
text-align: left !important;
margin: 5px !important;
}

LI.previous > A {
font: 14px "Helvetica", "Calibri" !important;
color: #999 !important;
text-shadow: 0px 1px 0px #000 !important;
width: 100% !important;
float: left !important;
border: 0px !important;
padding: 0px !important;
text-align: left !important;
}

DIV#daily-pagination {
font: 12px "Helvetica", "Calibri" !important;
color: #AAA !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV#daily-pagination > A {
font: 12px "Helvetica", "Calibri" !important;
color: #999 !important;
text-shadow: 0px 1px 0px #000 !important;
}

DIV#daily-pagination + H1 {
font: 20px "Helvetica", "Calibri" !important;
color: #EEE !important;
font-weight: bold !important;
text-shadow: 0px 1px 0px #000 !important;
text-align: center !important;
border: 0px !important;
margin: 0px !important;
padding: 0px !important;
}

DIV#daily-pagination + H1 + SCRIPT + DIV.date-change {
display: none !important;
}

}
