css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(crunchbanglinux.org), domain(www.crunchbanglinux.org) {

/* Inverse Crunchbang Forums 
User Style by Tunafish
21/11/2010
Please feel free to alter and post your code on the Crunchbang Forum

Item             Forum color:      Userstyle color:
Icon/Codebox     #222222           #E6E6E6
Background       #282828           #D8D8D8
Icon Closed      #333333           #CCCCCC
Icon Sticky      #575757           #A8A8A8
Font-color       #D8D8D8           #2B2B2B

 */

.main-content div, .main-content div, .posthead, .posthead h3, #brd-qpost, .main-subhead, #post-form, #post-preview, #topic-review, .postbody {
	background: #D8D8D8 !important; 
	color: #2B2B2B !important;
        }

.postbody *, .info-replies *, .info-forum *, .brd .posthead a {
	color: #2B2B2B !important;
        }

.brd .postfoot a, .brd .postbody a, .brd .main-item a, .brd .main-content ul li strong {
	color: #2B2B2B !important;
        }

.brd span.sig-line {
	border-color:#2B2B2B !important;
	}

.brd h2 {
       font-weight: bold !important;
       }

.brd h2 .item-info {
       font-weight: normal !important;
       }

.main-head .hn h2, .sticky {
       font-weight: bold !important;
	background:#D8D8D8 !important; 
	color:#2b2b2b !important;
       }

.brd .entry-content .quotebox, .brd .entry-content .codebox {
	background: #E6E6E6 !important; 
	border-color: #282828 !important;
	}


.brd .posthead a, .brd .postfoot a, .brd .postbody a, .brd .main-item a   {
    border:0 !important;
    color: #2B2B2B !important;
    text-decoration:underline !important;

    }

.brd .main-content * a:hover, .brd .main-content * a:active, .brd .main-content * a:focus {
    border:0 !important;
    color: #D8D8D8 !important;
    text-decoration:none !important;
    background: #2b2b2b !important; 
    }  

.brd .online .userstatus span {
	border-color: #2b2b2b !important;
	}

.brd .userstatus span {
	border-color: #E6E6E6 !important;
	}

.brd .main-content .main-item .hn .item-status em.sticky ,.brd .main-content .main-item .hn .item-status em.closed {
 	color: green !important;
 	font-weight: bold !important;
	color:#2b2b2b !important;
 	}

.brd .main-content .main-item .icon {
	border-color: #E6E6E6 !important;
	}

.brd .main-content .redirect .icon {
	border-color: #E6E6E6 !important;
	}

.brd .main-content .sticky .icon {
	border-color: #a8a8a8 !important;
	}

.brd .main-content .closed .icon {
	border-color: #CCCCCC !important;
	}

.brd .main-content .sticky .closed {
	border-color: #CCCCCC !important;
	}

.brd .main-content .new .icon {
	border-color: #2b2b2b !important;
	}
}
