css:
 @namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wer-kennt-wen.de") {

/*Changes for other colors >>>*/
.box.info {
background:#c81313 !important;
color: #fff !important; 
}

textarea {
border:1px solid #c81313!important;
}

h2 span a {
color:#c81313!important;
}

h2 span a:hover {
color:#ff0000!important;
}

#navigation a{
color:#c81313!important;
}

#navigation a:hover{
color:#fff!important;
}

a:hover{
color:#c81313!important;
font-weight:bold !important;
}

button {
border-color: #c81313 !important;
}
/* <<<< */


a {
color:#000!important;
}

body{
 background-color: #fff!important;
 background-image: url(http://wkw.mebuca.de/red/bg_red.png) !important;
 background-attachment: fixed !important;
}

#wkwHeader {
background:url("http://wkw.mebuca.de/red/bg_head.png") !important;
border: solid 1px #000 !important;
border-bottom: none !important;
width:758px !important;
margin-left: 6px!important;
}

#wkwHeaderWoodle {
background: none!important;
}

#wkwHeader .abs{
  visibility: hidden !important;
}

#main {
background:none !important;
border-left:1px solid #000 !important;
border-right:1px solid #000 !important;
padding:0 1px;
width:778px !important;
}

#navigation, #navbalken {
background:url("http://wkw.mebuca.de/red/bg_navi_black.png") !important;
margin: 0px -1px !important;
}

#navigation ul ul a {
border-bottom:3px solid #000!important;
}

#searchfield .searchContainer {
border:1px solid #000!important;
background:none!important;
}

#searchquery, #sbi, #googleQuery, #ebsearch {
background:none!important;
}

#submit_searchquery{
background:none!important;
}

table.people th, table.press th {
background-color:#000!important;
}



table.people tr.normal, table.press tr.normal {
background-color:#eeeeee!important;
}

table.people tr.odd, table.press tr.odd {
background-color:#cccccc!important;
}

form#writeMessage {
border:1px solid #000!important;
}

input {
border:1px solid #000!important;
}

.messagediv {
border:1px solid #dddddd!important;
color: #dddddd!important;
}

.messagediv td, .messagediv td strong {
color: #dddddd!important;
}

.messagediv-active {
border:3px solid #000!important;
}


#printPics {
background:none!important;
}

#imgContent {
background:none!important;
}

.box.info {
border-color:#000 !important;
}

.UI-activeForm {
background:none !important;
border:none !important;
}

button {
background-color:#000 !important;
line-height: 10px !important;
color: #fff !important;
}

h2 span {
background:url("http://wkw.mebuca.de/red/bg_navi_black.png") !important;
}

#shadow, #banner, #footer{
  background: none !important;
}


#alternative_flash_3{
 display:none!important;
}

#ftotdftdivid_300690{
  display:none!important;
}


#ads{
  display:none!important;
}

#topad{
  display:none!important;
}

#middlead{
  display:none !important;
}

table.photolist{
 width:100% !important;
}

#editme_allgemeines_info{
  display:none !important;
}

}

@-moz-document url(http://www.wer-kennt-wen.de/start),url-prefix(http://www.wer-kennt-wen.de/person),url-prefix(http://www.wer-kennt-wen.de/club) {
table.photolist td{
  width:16.7% !important;
}
}

@-moz-document url-prefix(http://www.wer-kennt-wen.de/people),url-prefix(http://www.wer-kennt-wen.de/club/people) {
table.photolist td{
  width:12.5% !important;
}
}
