/* 

   ================================================================================

 * Livejournal Modern

 *

 * Author         : BananaMuffin (eevereon)

 * Version        : 1.0.9

 * Customization  : Go to my journal for more colors replacements and other ways

                    to edit the style -

                    http://eevereon.livejournal.com/



                    To find code that has been changed since the last version,

                    search for UPDATE 109

   ================================================================================

*/ 



@namespace url(http://www.w3.org/1999/xhtml);



/* ================================ GLOBAL STYLE ================================ */



@-moz-document domain("livejournal.com") {



/* ~~~~~~~~~~~~~~ CUSTOMIZATION SECTION ~~~~~~~~~~~~~~ */

/* (don't remove the '!important') */



/* --- Images --- */



/* Banner image | max-height 81px */

/* change 'repeat-x' to 'no-repeat' if you don't want the banner image to repeat */

#NavigationWelcomeLogo { 

 background-image: url(http://objsam.files.wordpress.com/2008/04/nebula_rcw49_04lrg.jpg); 

 background-repeat: repeat-x;

}



/* Background image */

.logged-in > #big-content-wrapper, .logged-out > #big-content-wrapper {

 background: url(http://objsam.files.wordpress.com/2008/04/nebula_rcw49_04lrg.jpg) #F4F4F4!important;

}



/* Theme color replace */

/* Logo and button backgrounds */

#Logo, input[type^=submit], input[type^=button],

input[type^=submit]:hover, input[type^=button]:hover,

.NavMenuItem:hover { 

 background-image: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/BlueImgs.png)!important;

}





/* --- Colors --- */



/* Links inside content containers */

.logged-in #content-inner a, .logged-out #content-inner a {

 color: #315D83!important;

}



.logged-in #content-inner a:hover, .logged-out #content-inner a:hover {

 color: #565656!important;

 text-decoration: underline!important;

}



/* Contextual popup links */

.logged-in > .ContextualPopup > .Inner a {

 color: #565656!important;

}



.logged-in > .ContextualPopup > .Inner a:hover {

 color: #999!important;

}



/* Background color for navigation menu hover */

.NavMenuSubNav li:hover {

 background: #0084AF!important;

}



/* Buttons background, border and text colors */

input[type^=submit], input[type^=button] { 

 background-color: #0A4C70!important; 

 border: 1px solid #0F6496!important;

 color: #eee!important;

}



input[type^=submit]:hover, input[type^=button]:hover { 

 background-color: #212121!important;

 border: 1px solid #4B4A47!important;

 color: #aaa!important;

}

/* end Theme color replace */



/* --- Icons --- */

/* UPDATE 109 */ /* removed */



/* ~~~~~~~~~~~~ end CUSTOMIZATION SECTION ~~~~~~~~~~~~~~~ */



body { cursor: default; }



::-moz-selection { background: #ddd; color: #666;}



/* Logo image */

#Logo { 

background-color: transparent;

background-position: 0 0;

background-repeat: no-repeat;

top: 0px!important;

left: 50%!important;

width: 206px!important;

height: 56px!important;

margin-left: -490px!important;

}



/* Navigation strips */

#navigation { 

background: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/Nav-Bg.gif) repeat-x #000!important;

height: 143px!important;

}



#NavigationWelcomeLogo { 

background-color: transparent;

height: 81px!important;

top: 53px!important;

}



#NavBar {

background: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/Menu-Bg.png) repeat-x transparent!important;

max-width: 970px;

height: 46px!important;

position: relative!important;

top: 7px!important;

margin: 0 auto!important;

padding-top: 10px!important;

padding-left: 20px!important;

}



#NavBar a {

letter-spacing: -1px;

color: #C8C8C8!important;

}



.NavMenuItem {

border: 1px solid transparent;

}



.NavMenuItem:hover {

background-position: -213px -36px!important;

background-color: transparent;

border: 1px solid #B1AFAB;

}



.NavMenuItem a, .NavMenuItem a:hover {

background: transparent!important;

font-weight: normal!important;

font-size: 14px!important;

}



.NavMenuItem ul {

background: #1A1A1A!important;

margin-left: -8px!important;

margin-top: 1px!important; /* UPDATE */

}



.NavMenuSubNav li {

background: #1A1A1A!important;

font-size: 10px!important;

padding: 2px!important;

}



#NavMenuSearch {

float: right!important;

top: 10px!important;

}



#NavMenuSearch > form > #SearchText {

width: 120px!important;

}



#NavMenu {

display: table-cell!important;

padding: 0px!important;

}





/* User information */

#welcome > h2 > .ljuser {

font-size: 18px!important;

}



#Userpic {

position: relative!important;

width: 0px!important;

height: 0px!important;

margin: 0 auto!important;

}



#defaultpic {

position: relative!important;

top: 8px!important;

right: -444px!important;

width: 35px!important; 

height: 35px!important;

-moz-border-radius: 5px; 

border: 1px solid #fff;

opacity: 0.9;

}



#defaultpic:hover {

opacity: 1.0;

}



#welcome {

background: transparent!important;

width: 285px!important;

height: 94px!important;

position: relative!important;

top: -54px!important;

left: 160px!important;

margin: 0 auto!important;

padding-right: 15px!important;

padding-left: 30px!important;

color: transparent!important;

text-align: left!important;

}



#welcome a {

color: #aaa!important;

}



#welcome h2 {

display: block!important;

position: relative;

top: 3px;

left: -60px;

}



#WelcomeLinks {

background: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/BlueImgs.png) transparent -197px -70px!important;

display: block!important;

overflow: hidden;

position: absolute!important;

height: 2px!important;

width: 39px!important;

padding-top: 15px!important;

top: 44px;

left: 448px;

text-align: center;

}



#WelcomeLinks:hover {

background-position: -236px -70px!important;

width: 190px!important;

height: 27px!important;

margin-left: -150px!important;

}



#WelcomeViewLinks {

position: absolute!important;

width: 350px!important;

top: 15px!important;

left: 85px!important;

text-align: center!important;

}



/* Contextual popup */

.logged-in > .ContextualPopup {

opacity: 0.9;

}



.logged-in > .ContextualPopup > .Inner {

background: #E1E1E1!important;

border: 1px solid #ddd!important;

-moz-border-radius: 5px;

color: #888!important;

}



.logged-in > .ContextualPopup > .Inner a {

font-size: 11px!important;

font-weight: normal!important;

text-decoration: none!important;

}



.logged-in > .ContextualPopup > .Inner a:hover {

text-decoration: none!important;

}



.logged-in > .ContextualPopup > .Inner > .Content {

background: #fff!important;

padding: 10px!important;

border: 0px!important;

-moz-border-radius: 5px;

color: #333!important;

}



.logged-in > .ContextualPopup > .Inner > .Content > .Relation {

text-transform: uppercase;

color: #565656!important;

}



.logged-in > .ContextualPopup > .Inner > .Userpic > a > img {

width: 40px!important;

height: 40px!important;

}





/* Content containers */

.logged-in > #big-content-wrapper {

padding-top: 50px!important;

color: #333!important; 

}



.logged-out #big-content-wrapper #content {

background: #E1E1E1!important;

position: relative!important;

max-width: 970px;

min-height: 630px;

margin: 50px auto!important;

-moz-box-shadow: 0px 0px 1px rgba(100, 100, 100, 1.0);

padding: 8px!important;

}



.logged-in #big-content-wrapper #content { 

background: #E1E1E1!important;

position: relative!important;

max-width: 970px;

min-height: 630px;

margin: 0 auto!important;

border: 1px solid #fff!important;

-moz-box-shadow: 0px 0px 1px rgba(100, 100, 100, 1.0);

padding: 8px!important;

}



.logged-in > #big-content-wrapper > #content > #content-inner,

.logged-out > #big-content-wrapper > #content > #content-inner {

background-color: #fff;

min-height: 630px;

border: 1px solid #fff!important;

padding: 15px!important;

font-family: arial!important;

}



#content-inner .logged-in, 

#content-inner .logged-out,

.logged-in > #big-content-wrapper > #content > #content-inner > .adtype-medrect > h1,

.logged-in > #big-content-wrapper > #content > #content-inner > .logged-in > div > h1,

.logged-out > #big-content-wrapper > #content > #content-inner > .adtype-medrect > h1,

.logged-out > #big-content-wrapper > #content > #content-inner > .logged-out > div > h1,

.search-forms h1 {

font-family: georgia!important;

font-weight: normal!important;

font-size: 24px!important;

color: #181614!important;

}



/* Header */

.section_head, .alternating-rows th {

background: #E1E1E1!important;

border: 1px solid #BEBEBE!important;

font-family: georgia!important;

font-weight: normal!important;

font-size: 18px!important;

color: #444!important; 

}



/* Page number navigation */

.search-result ul li ul li strong {

background: #E1E1E1!important;

border: 1px solid #BEBEBE!important;

color: #333!important;

}



/* Pages list statistics */

.pages-list li {

background: #E1E1E1!important;

border-bottom: 2px solid #EFEFEF!important;

-moz-border-radius-topleft: 5px;

-moz-border-radius-topright: 5px;

color: #333!important;

}



/* Fix for entry boxes with forms */

#content-wrapper > form > table { width: 100%!important; }



/* Fix for entry container in unstylized comment pages */

#content-wrapper > div[style^="margin-left"] {

margin-left: 0px!important;

padding: 10px!important;

}



/* Fix for content container for every other page */

.adtype-skyscraper, .adtype-medrect { width: 100%!important; }

.adtype-medrect > div > .standout > .container { margin-left: 30px!important; text-align: center!important; }



/* Footer */

.logged-in #footer {

position: relative!important;

background: transparent!important;

margin: 140px 0px 0px 0px!important;

width: 100%!important;

border: 0px!important;

}



.logged-out #footer {

position: relative!important;

background: transparent!important;

margin: 80px 0px 0px 0px!important;

width: 100%!important;

border: 0px!important;

}



#footer-links {

background: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/Footer-Bg.gif) repeat-x #0E0E0E!important;

border: 0px!important;

color: #333!important;

}



#footer-links a, #footer-links li {

color: #666!important;

}



#footer-links h3 {

text-transform: uppercase!important;

font-weight: normal!important;

color: #aaa!important;

}



#footer-links table {

margin-top: 15px!important;

}



#footer-copyright {

background: #000!important;

padding: 10px 0 10px 0!important;

margin: 0px!important;

color: #333!important;

}





/* Log in box */

#Login {

background: transparent!important;

top: -3px!important;

}



.lj_login_form {

background: #000!important;

-moz-border-radius: 5px;

color: #999!important;

opacity: 0.85;

}



.lj_login_form a {

color: #aaa!important;

}





/* Unstylized comment pages */

/* UPDATE 109 */ /* fixed comment header colors, removed redundant code */

.talk-comment-head {

background: #eee!important;

padding: 0px!important;

margin-bottom: 10px!important;

}



#qrform table {

background: #eee!important;

padding: 5px 25px 5px 5px!important;

border: 0!important;

width: 100%!important;

}





/* Profile page */

.username, .section { 

background: #EFEFEF!important; 

padding: 5px!important; 

border: 1px solid #E1E1E1!important; 

}



.username { width: 99%!important; font-size: 14px!important; }



.actions li { 

background: #EFEFEF!important; 

padding: 5px 10px 5px 10px!important; 

margin: 0 2px 0 2px!important; 

border: 1px solid #E1E1E1!important; 

display: inline-block!important; 

width: auto!important; 

max-height: 17px; 

overflow: hidden; 

-moz-border-radius-topleft: 5px; 

-moz-border-radius-topright: 5px; 

}



.actions li:hover { background: #E1E1E1!important; }

.actions li img { display: none; }

.actions { position: relative; float: right; top: -57px; }

.user_details .user_details_inner { width: 100%!important; }





/* Page elements */

hr { background: #EFEFEF!important; }



.standout > .container {

background: transparent!important;

border: 0px!important;

}



.blue { background: #EFEFEF!important; border: 1px solid #ccc!important; }



/* Input buttons  */

input[type*=submit], input[type*=button] { 

-moz-appearance: none;

background-position: -213px 0!important;

padding: 1px 7px 1px 7px!important;

-moz-border-radius: 5px;

}



input[type*=submit]:hover, input[type*=button]:hover { 

-moz-appearance: none;

background-position: -213px -18px!important;

padding: 1px 7px 1px 7px!important;

}



/* Icons */

/* UPDATE 109 */ /* removed */



/* Page headers icons */

/* as of v. 1.0.6, all icons next to title headers has been removed, */

/* if you want to restore them, comment out the following block or delete it */



.homepage-account h2 span,

.appwidget-searchjournals h2 span,

.appwidget-updatesforuser h2 span,

.appwidget-upgradeaccount h2 span,

.appwidget-vgifts h2 span,

.appwidget-friendbirthdays h2 span,

.appwidget-featuredstyles h2 span, 

.appwidget-friendupdates h2 span,

.appwidget-shop-paymentmethods h2 span,

.appwidget-journalspotlight h2 span,

.appwidget-recentcomments h2 span,

.appwidget-addcommunity h2 span,

.appwidget-search h2 span,

.appwidget-polloftheday h2 span

{ background-position: -271px -18px!important; padding-left: 0px!important; }





/* --- Remove ads --- */

.ljadwrapper-app-vertical, .ljad { display: none!important; }

}



/* ================================ end GLOBAL STYLE ================================ */





/* =================================================================================== */





/* ================================ PAGE SPECIFIC ================================ */



/* --- Inbox --- */

@-moz-document url-prefix("http://www.livejournal.com/inbox/") {

.inbox #ActionRow1 { display: table; position: absolute; top: -26px; }

.inbox #ActionRow2 { display: table; position: absolute; }



.actions { display: block; position: relative; top: 0px; left: 0px; }

.actions input[name*="markRead"],

.actions input[name*="markUnread"],

.actions input[name*="delete"] 

{ display: inline-block; position: relative; left: -400px; }



#all_Table { margin-top: 30px; }



.InboxItem_Content { margin: 10px 0 50px 0!important; width: 112%!important; }

.InboxItem_Content .actions { margin: 25px 0 0 0; color: transparent; position: relative; top: 100%; }

.InboxItem_Content .actions a { display: inline-block; padding: 5px; background: #eee!important; border: 1px solid #ccc; }

.InboxItem_Content .actions a:hover { background: #fff!important; }



.ManageButtons { margin: 0 -20px 0 10px!important; }



#allForm > div[style*="margin-top"] { margin-top: 50px!important; }



.NotificationTable tr.Selected { background-color: #eee!important; }



#esn_folder_all { background-color: #eee!important; }

#esn_folder_all:hover { background-color: #eee!important; }

}





@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=usermsg_recvd") { 

#esn_folder_usermsg_recvd { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; } }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=friendplus") { 

#esn_folder_friendplus { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; }  }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=birthday") { 

#esn_folder_birthday { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; }  }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=befriended") { 

#esn_folder_befriended { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; }  }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=entrycomment") { 

#esn_folder_entrycomment { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; }  }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=bookmark") { 

#esn_folder_bookmark { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; }  }

@-moz-document url-prefix("http://www.livejournal.com/inbox/?view=usermsg_sent") { 

#esn_folder_usermsg_sent { background-color: #eee!important; }

#esn_folder_all { background-color: transparent!important; } }





/* --- Community directory --- */

@-moz-document url-prefix("http://www.livejournal.com/browse/") {

#big-content-wrapper #content { 

width: 970px!important;

}



.l-aside { margin-left: 15px!important; }

.l-content { width: 53%!important; }

.l-sidebar { width: 250px!important; }

}





/* --- Shop --- */

@-moz-document url-prefix("http://www.livejournal.com/shop/") {

#big-content-wrapper #content { 

width: 970px!important;

}



.appwidget-shop-walletstatus div { background: #EFEFEF!important; }

.appwidget-shop-walletstatus .appwidget { border: 1px solid #E1E1E1!important; }



.m-shop-tabs > li { 

background: #EFEFEF!important; 

border-left: 1px solid #E1E1E1!important; 

border-top: 1px solid #E1E1E1!important;

border-right: 1px solid #E1E1E1!important;

}



.m-shop-tabs > .current { background: #fff!important; }



.b-promo-paidaccount a span { color: #fff!important; }

.b-promo li { border: 1px solid #E1E1E1!important; }



.b-gifts-title { 

background: #EFEFEF!important; 

padding: 0px!important; 

border: 0px!important; 

text-align: center!important; 

}



.b-gifts-title h2 { display: block!important; top: 12px; position: relative; }

}





/* --- Writer's Block view answers page --- */

@-moz-document url-prefix("http://www.livejournal.com/misc/latestqotd.bml") {

.appwidget-qotd { 

background: #EFEFEF!important; 

border-bottom: 0px!important; 

}



.b-qotd-question-inner h3 {

color: #000!important;

}

}





/* --- Edit an entry page --- */

/* UPDATE 108 */ /* restored default settings, but made the edit box bigger */

@-moz-document url-prefix("http://www.livejournal.com/editjournal.bml") {



.skyscraper { display: none!important; }



#spellcheck-results { border: 1px solid #ccc!important; position: relative; top: -140px; }



#entry-form-wrapper { 

width: 935px!important;

margin-left: 0px!important;

margin-right: 50px!important;

}



#entry-tabs li a {

border: 1px solid #BEBEBE!important;

-moz-border-radius-topleft: 5px;

-moz-border-radius-topright: 5px;

}

}





/* --- Post an entry page --- */

/* UPDATE 108 */ /* restored default settings, but made the edit box bigger */

@-moz-document url-prefix("http://www.livejournal.com/update.bml") {



.skyscraper { display: none!important; }



#spellcheck-results { border: 1px solid #ccc!important; position: relative; top: -140px; }



#entry-form-wrapper { 

width: 935px!important;

margin-left: 0px!important;

margin-right: 50px!important;

}



#entry-tabs li a {

border: 1px solid #BEBEBE!important;

-moz-border-radius-topleft: 5px;

-moz-border-radius-topright: 5px;

}

}





/* --- Settings page --- */

@-moz-document url-prefix("http://www.livejournal.com/manage/settings/") {

#settings_nav li a {

background: #E1E1E1!important;

border: 2px solid transparent!important;

-moz-border-radius-topleft: 5px;

-moz-border-radius-topright: 5px;

color: #333!important;

}



#settings_nav li .active {

background: #333!important; 

color: #999!important;

}



#settings_nav_title p {

background: #EFEFEF!important;

}



#settings_save {

background: transparent!important;

text-align: center;

}



#settings_save input {

width: 100px;

}

}





/* --- Userpic page --- */

@-moz-document url-prefix("http://www.livejournal.com/editpics.bml") {

#uploadBox { border: 1px solid #E1E1E1!important; }

#uploadBox-inner { background: #EFEFEF!important; }

}





/* --- Home page --- */

@-moz-document url-prefix("http://www.livejournal.com/") {

#content-inner {

background-color: #fff!important;

}



/* Remove ontd recent posts columns */

.homepage-loggedin > div > .l-wrap { display: none!important; }



/* Home page widgets */

#content-wrapper h2 { 

background: transparent!important; 

font-family: georgia!important;

font-weight: normal!important;

font-size: 18px!important;

color: #444!important; 

}



.homepage-account { 

background: #fff!important;

}



#LJWidget_9 {

border-bottom: 1px solid #eee!important;

}



.w-head {

background: #fff!important;

}

}





/* --- Search page --- */

@-moz-document url-prefix("http://www.livejournal.com/") {

.search-result-in { width: 100%!important; }

}



/* --- Journal Style page --- */ 

@-moz-document url-prefix("http://www.livejournal.com/customize/") {

.theme-nav-content, .on a, .customize-content { border: 0px!important; }



.theme-current, .customize-nav { background: #eee!important; }

.theme-current-links { border-bottom: 1px solid #ddd!important; border-right: 1px solid #ddd!important; }

.theme-nav-inner-wrapper { background: #fff!important; }

.customize-inner-wrapper { background: #fff!important; }

.customize-inner-wrapper ul { border: 0px!important; }

.theme-nav-cat, .customize-nav-group { border-bottom: 1px solid #ddd!important; border-right: 1px solid #ddd!important; }

.theme-selector-nav { background: #eee!important; }

.theme-nav-separator { background: #fff!important; border-bottom: 1px solid #ddd!important; border-right: 1px solid #ddd!important; }

.theme-nav-separator hr { display: none!important; }

.current { background: #eee!important; border: 1px solid #ddd!important; }



.moodtheme-preview { background: #eee!important; border: 1px solid #ddd!important; }

.subheader { background: #eee!important; border: 1px solid #ddd!important; padding-left: 5px!important;  }

.subheader:before { content: "? "; }

.prop-list td { width: 100%!important; }

}



/* --- Fix for preview entry page --- */

@-moz-document url-prefix("http://www.livejournal.com/preview/entry.bml") {



#content { 

background: transparent!important;

padding: 0px!important;

}

}





/* --- Scrapbook pages --- */

@-moz-document domain("pics.livejournal.com") {



/* Navigation strips */

body { 

background: url(http://i352.photobucket.com/albums/r341/Rentoryuu/LJ/Background-HanddrawnOrnate.gif); 

font-family: arial!important;

color: #181614!important;

