css:
#appmenu-button-container {
     position: fixed !important;
     margin-top: 0 !important;
     margin-left: 2px !important;
}
#appmenu-button {
     border: 1px solid rgba(90,30,0,0.8) !important;
     border-top: none !important;
     padding: 1px 5px 1px 5px !important;
     height: 20px !important;
     min-width: 40px !important;
     width: 40px !important;
}

#appmenu-button .button-text { 
     color: transparent !important;
     text-shadow: none !important;
     margin-left: -23px !important;
     margin-right: -35px !important;
     background: url("data:image/png;base64,%3D%3D") no-repeat 16px !important;
}

#appmenu-button .button-menu-dropmarker {
     list-style-image: url("data:image/png;base64,%3D") !important;
}
menubar, toolbox, toolbar, .tabbrowser-tabs {
background-color: transparent !important;
}
#navigator-toolbox[tabsontop="true"] #TabsToolbar {
padding: 3px 110px 0 42px !important;
margin-top: -29px;
}
#main-window[sizemode=maximized] #navigator-toolbox[tabsontop="true"] #TabsToolbar {
margin-top: -24px;
}
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
menubar > menu > .menu-iconic-text {
display: none !important;
}


/*tabs*/
.tabs-alltabs-box {
display: none !important;
}
.tabbrowser-tabs *|tab[busy] .tab-icon-image {
display: none !important;
}
.tabbrowser-tabs .tab-text {
font-size: 80% !important;
}
.tabbrowser-tabs .tab-icon-image {
opacity: 1.0 !important;
display: -moz-box !important;
-moz-box-ordinal-group: 2 !important; // Left side
}
.tabbrowser-tabs *|tab:not([image]) .tab-icon {
display: none;
}
.tabbrowser-tab:not([selected="true"]) > .tab-text {
visibility: hidden !important;
min-width: 1px !important;
width: 1px !important;
display: none !important;
}
.tabbrowser-tab:not([selected="true"]) {
min-width: 32px !important;
max-width: 32px !important;
}
.tabbrowser-tab[selected="true"] {
max-width: 40px !important;
min-width: 40px !important;
width: 100% !important;
}
.tabbrowser-tabs *|tab:hover {
max-width: 40px !important;
min-width: 40px !important;
width: 100% !important;
}
.tabbrowser-tab:not([selected="true"]):hover > .tab-text {
display: visible !important;
visibility: visible !important;
min-width: 40px !important;
width: 90% !important;
display: block !important;
}
