css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://eu.battle.net/wow/en/forum/'), url-prefix('http://eu.battle.net/wow/fr/forum/'), url-prefix('http://eu.battle.net/wow/de/forum/'), url-prefix('http://eu.battle.net/wow/es/forum/'), url-prefix('http://eu.battle.net/wow/ru/forum/') ,url-prefix('http://us.battle.net/wow/en/forum/'), url-prefix('http://us.battle.net/wow/es/forum/'),url-prefix('http://eu.battle.net/wow/en/search'), url-prefix('http://eu.battle.net/wow/fr/search'), url-prefix('http://eu.battle.net/wow/de/search'), url-prefix('http://eu.battle.net/wow/es/search'), url-prefix('http://eu.battle.net/wow/ru/search') ,url-prefix('http://us.battle.net/wow/en/search'), url-prefix('http://us.battle.net/wow/es/search')    
{
#station-content {background-color:#ddd!important; }
.post-interior, .forum-link, .sidebar-content, .search-result { background-color:#ececec !important; }
#popular-topics .readmore, #forum-list .forum-parent, #station-content .coc { color:#069! important;}
#station-content .coc a {color:#069!important; text-decoration:underline!important}

.search-result .by-line, .search-result .result-title, .search-content {color:#333!important;}
.search-result a {color:#069!important;}
.search-result .result-title {border-bottom: 1px solid black!important;}

#forum-list, {background-color:#ddd!important; }
.forum-link, .forum-link:hover span{color:#416699!important;}
.forum-link .desc {color:#333!important;}
.sidebar-content .title {color:#416699!important;}
.sidebar-content .desc {color:#333!important;}
.filter-show-all  {color:#333!important;}
.filter-show-all a {color:#069!important; text-decoration:underline!important}

#posts, #posts tr:hover, #posts-container  { background-color:#ececec !important; }
#posts .post-title a {color:#416699!important;}
#posts .post-title a:hover {text-decoration:underline!important;}
#posts .type-blizzard { color:blue!important;} 
#posts .post-status {color: grey!important;}
#posts tr.mvp .post-author, #page-comments .mvp .context-link { color:#0a1; }

#thread .post-detail a {color:#069!important; text-decoration:underline!important}
#thread .mvp .post-detail, #page-comments .mvp .content  { color:#0a1!important;}
#thread .mvp .post-detail blockquote { border-color:#0a1!important; border-bottom-color:#83bb57; }
#thread .blizzard .post-detail, .blizzard .post-edited, #page-comments .blizzard .content {color: #03f !important;}
#thread .blizzard .post-detail blockquote { border-color:#03f!important; border-bottom-color:#002555; }

#thread .post-detail{color:#333!important;}
#thread .user-name .context-link {color:azure!important; }
#thread .achievements {color:yellow!important;}
#thread .post-user {background-color:#5B7382!important;
  border-top: 3px solid #ececec!important; 
  border-right: 7px solid #ececec!important;
  border-left: 3px solid #ececec!important;}

#forum-content .forum-actions, #forum-content .section-header {background-color:#ececec!important;}
#forum-content .forum-actions.bottom {background-color:#1a0f08!important;}
#forum-content .section-header {color:#416699!important;}

.post{ 
  border-bottom: 2px solid #444!important; 
  border-top: 2px solid #444!important; 
  border-left: 3px solid #190E08!important;
  border-right: 3px solid #190E08!important;}
.post-info .postData .date, .post-edited { color:#636363; }
.post-info .postData, .post-info .postData a { color:#069!important; }
.high-rated .post-info .date { color:#636363!important; }
.post .mvp-title {color:#0a1!important;}

.high-rated { background-image:none!important; }

.forum-options a { color:#069!important; text-decoration:underline!important; }
div.forum-options a.active { color:#333!important; text-decoration:none!important; }
.forum-options a:hover { color:blue!important; }

.karma-feedback {background-color:#5B7382!important; color:#ddd!important;}
.karma-feedback  a {font-weight:bold}

}
