css:
@-moz-document domain(icmag.com) {
body{
background: black !important;
}

div.smallfont > table > tbody > tr > td > a.yellow > font:first-child{
color: #000 !important;
}

div.page > div > table:first-child  a img:first-child{
display:none;
border:2px solid red !important;
}
div.page > div > table:first-child  a:first-child{
display:block;
width:665px;
height:110px;
background: url(https://www.icmag.com/ic/picture.php?albumid=24801&pictureid=539153) !important;
}  
div.page div table:nth-child(13){
background:#545030 !important;
border:1px #545030 !important;
}
div.page div table:nth-child(13) td{
background:#545030 !important;
border:1px #545030 !important;
}

.page {
border-color:grey !important;
background:#545030 /*url(https://www.icmag.com/ic/picture.php?albumid=24801&pictureid=538403)*/ !important;
}
.tfoot {
background:#e7e7e7 !important;
}
.tfoot a{
color: #000 !important;
}
.alt1 td{
background:lightgrey !important;
color:#ffffff !important;
}
.tcat {
background:#999900 !important;
color: #fff !important;
-moz-border-radius:7px 7px 7px 7px !important;
border-color:#000000 #000000 #000000 #000000 !important;
border-style:solid solid solid solid !important;
border-width:1px 1px 1px 1px !important;
}
.thead {
background:#666600 !important;
color: #fff !important;
-moz-border-radius:7px 7px 7px 7px;
border-color:#000000 #000000 #000000 #000000 !important;
border-style:solid solid solid solid !important;
border-width:1px 1px 1px 1px !important;
}
.vbmenu_control {
background: #666600 !important;
-moz-border-radius:7px 7px 7px 7px;
border-color:#000000 #000000 #000000 #000000 !important;
border-style:solid solid solid solid !important;
border-width:1px 1px 1px 1px !important;
}
.alt2, .alt2Active {
background:none repeat scroll 0 0 #FBFBFD !important;
}
.button {
-moz-border-radius:7px 7px 7px 7px;
background:#000 !important;
/*url("../../ic/images/buttons/-wetbutton.jpg")
repeat-x scroll left top transparent !important; */
background-color:#666600 !important;
border-color:#000000 #000000 #000000 #000000 !important;
border-style:solid solid solid solid !important;
border-width:1px 1px 1px 1px !important;
color:#000000 !important;
font:bold 11px verdana,arial,helvetica;
margin-bottom:5px !important;
padding:3px 3px 3px !important;
}
select {
-moz-border-radius:5px 5px 5px 5px;
background:none repeat scroll 0 0 #666600 !important;
border-color:#000000;
border-style:solid;
border-width:1px 1px 3px;
color:#000000 !important;
font:bold 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
margin-right:5px;
padding:2px 3px 3px;
}
.fjdpth0,.fjdpth1,.fjdpth2,.fjdpth3,.fjsel {
background:none repeat scroll 0 0 grey !important;
color:#000;
}
.vbmenu_hilitea:hover, .vbmenu_hilitea:active, .vbmenu_option_hilite,
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
color:#000000 !important;
text-decoration:none;

background:lightgrey !important;
}
.vbmenu_popup {
-moz-border-radius:6px 6px 6px 6px;
background:none repeat scroll 0 0 #000 !important;
border-color:black !important;
border-style:solid;
border-width:1px 5px 3px 3px;
color:#FFFFFF;
margin-left:10px;
margin-top:5px;
}
.vbmenu_option {
background:none repeat scroll 0 0 #fff !important;
border:medium none;
color:#000000;
cursor:pointer;
font:11px verdana,arial,helvetica;
white-space:nowrap;
}
.vbmenu_hilite a:link, .vbmenu_hilite,.vbmenu_option a:link, .vbmenu_option_alink {
color:#000000 !important;
text-decoration:none;
background:lightgrey !important;
}

}
