@namespace url(http://www.w3.org/1999/xhtml);

/* TITLE: Facebook  - http://userstyles.org/styles/40331 */

/* INITIAL Original by Davey Jacobson  userstyle - http://userstyles.org/styles/36151 */

/* NEW MOD: Juampi_Mix - Juampi_Mix@live.com.ar - http://userstyles.org/users/63516 */

@-moz-document domain("facebook.com") {


/***** GLOBAL OVERRIDES *****/
.UIComposer_InputShadow > div { overflow-y:hidden!important; }
#gb_muffin_area, #pagelet_netego_ads, DIV#sidebar_ads, .phs { display: none !important; }
#rightCol { display: none; }

/* <-----REMOVE FROM CODE BELOW FOR LARGER FONT-----> */
/* .UIImageBlock_Content, .UIImageBlock_Content > * { font-size: 13px !important; } */


/***** HOME PAGE & LEFT-COLUMN PAGES *****/

/*** NEWS FEED ***/
.home #pagelet_stream_header, .home #pagelet_stream_pager { width: 760px !important; }
.home .UIComposer_Content { width: 760px !important; }
.home ul#home_stream li.pvm { width: 725px !important; }
.home #home_stream, .home #home_stream .uiUfi { width: 700px !important; }
.home #home_stream .uiUfi .uiUfiAddComment > textarea { width: 642px !important; }
.home #home_stream .hideSelector { position: relative; left: -34px !important; }
.nonshift .mall_post_image_block_content .uiUfi { max-width: 423px !important; }

/*** SEARCH RESULTS ***/
.search #contentArea { width: 770px !important; }

/* POP-UP FOR SHARE */
.UISharer_Body div.UIComposer_Content { width: 562px !important; }
.UISharer_Body .UIShareComposer_Input { width: 480px !important; }
.UISharer_Body div.Mentions_Input { width: inherit !important; }

/* MESSAGES */
.home #gigaboxx_composer .UIComposer_Content { width: 700px !important; }

/* POP-UP COMPOSER */
.profile .pop_content .MessageComposer .UIComposer_Content { width: 420px !important; }


/***** ITEMS VIEWED FROM NOTIFICATION MENU *****/

/*** COMMENTS ***/
.ego_page.fbx .uiUfi { width: 660px !important; }
.ego_page.fbx .uiUfi .uiUfiAddComment > textarea { width: 604px !important; }

/***** PROFILE PAGE *****/
.profile .profile_top_bar_container { width: 737px !important; }
.profile .profile_name_and_status { width: 737px !important; }

.profile #tab_canvas { margin-right: 0 !important; width: 737px !important; }

/* TAB FIX FOR SHRUNKEN TABS - thanks to Juampi_Mix */
.profile #profile_tabs, DIV.tabs.clearfix { height: auto !important; }

.profile .uiUfi { margin-right: 0 !important; width: 677px !important; }
.profile .uiUfi .uiUfiAddComment > textarea { width: 621px !important; }


/***** PICTURES *****/
.fbxAlbum {overflow-x: hidden !important; }
.fbxAlbum .fbPhotosGrid { width: 110% !important; }
.photo .uiUfi { width: 530px !important; }
.photo .uiUfi .uiUfiAddComment > textarea { width: 474px !important; }


/***** EVENTS PAGE *****/

/*** FROM LEFT COLUMN ***/
.home #mainContainer #contentCol.hasRightCol #contentArea > div > div { width: 760px !important; }

.event_profile .UIComposer_Content { width: 770px !important; }

.ego_page.event_profile .uiUfi { width: 710px !important; }
.ego_page.event_profile .uiUfi .uiUfiAddComment > textarea { width: 653px !important; }


/*** CHAT ***/
.fbChatBuddylist .idle, .chatIdle { display: none !important; }

/*** Reestile borders ***/
A, LABEL, LI, BORDER, INPUT, IMG, DIV, TEXTAREA, SPAN, .uiTypeahead, BUTTON, H1, H2, H3, H4, H5, AB, B, UL, .box_header.UITitle.UITitle_h5, SELECT
{-moz-border-radius: 10px  !important}


.right_column_container.clearfix
{margin-left: 30px !important}

DIV.profile_color_bar.vcard
{background-color: #FFF  !important;}



}
