@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kubalibre.com") {

div.panel {
  position: fixed !important;
  z-index: 10 !important;
  background: -moz-linear-gradient(top,  #0b2b74,  #0b2b54) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#00b2b74), to(#0b2b54)) !important;
}

div.head .palma, div.head .kuba {
  display: none !important;
  background: none !important;
}

