  background: #004488 !important;
  height: 28px !important;
  position: fixed !important;
  z-index: 10 !important;
  width: 100% !important;
  top: 40px !important;
  background: -moz-linear-gradient(top,  #048,  #06B) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#048), to(#06B)) !important;
  border-bottom: 1px solid #03a !important;
}

.menu ul {
  margin-top: 4px !important;
}

.menu ul li a {
  padding-top: 2px !important;
  padding-bottom: 2px !important;
}

