css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://us.battle.net/wow/),
               url-prefix(http://eu.battle.net/wow/),
               url-prefix(http://kr.battle.net/wow/),
               url-prefix(http://sea.battle.net/wow/) {
	/* Forum view */

	.forum-post-icon {
		background-position: 15px -2px !important;
		height: 22px !important;
	}
	#posts .post-title a, #posts .post-status, #posts .post-pageNav .pageNav {
		padding-top: 0 !important;
		padding-bottom: 0 !important;
	}
	.forum-post-icon .blizzard_icon {
		height: 24px !important;
		position: relative;
		left: -10px;
	}
	#posts .last-read {
		background-position: 50% -99px !important;
		height: 18px !important;
	}
	#posts .post-replies, #posts .post-views { color: #A68450 !important }
	#thread .blizzard { background-position: 0 -16px !important }
	#posts tr:nth-child(even) { background-color: #23140B !important }
	#posts tr:hover { background-color: #2D1809 !important }
  
	/* Thread view */
  
	.post-user, .talkback .post-user {
		height: 105px !important;
		background-position: 0 -16px !important;
	}
	.blizzard .post-user { background-position: 0 -288px !important }
	.post-user .avatar, .post-user .character-info { top: 10px !important }
	.post-info-int { padding: 7px 19px 0 0 !important }
	.post-options { padding: 0 20px !important }
	.post-detail { padding: 10px 0 5px !important }
}
