css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tiipsi.com") {

BODY, TD {
font-family : VERDANA !important ;
font-size : 10px !important;
color: darkblue !important; 
background-color: silver !important;
}

A {
text-decoration: none !important;
color: darkred !important;
}
A:Hover {
text-decoration: underline !important;
color: red !important;
}

input {
color: black !important;
background-color: silver !important;
border: thin solid darkred !important;
}
input:hover {
border: thin solid orange !important;
}
input:focus {
background-color: white !important;
border: thin solid orange !important;
}

.black {
font-family: VERDANA !important ;
font-size: 11px !important;
}

.silver {
font-family: VERDANA !important ;
font-size: 10px !important;
color: silver !important;
}

.statField {
	background: silver !important;
	border: 1px solid #115533 !important;
}

.str {
	font-family: Verdana, Lucida, Georgia !important;
	color: #106E44 !important;
}

#topmenucell {
border: 1px solid #DDFF66 !important; 
background-color: lightblue !important;
color: black !important;
font-weight: bold;
}

#bottommenucell {
border-left: 1px solid green !important;
border-bottom: 1px solid green !important;
border-right: 1px solid green !important;
border-top: 1px solid green !important;
padding: 5px !important;
background-color: lightgrey !important
}

#footer {
padding: 0 !important;
font-family: VERDANA !important ;
font-size: 10px !important;
color: #000000 !important;
}

.chatname {
font-size: 9px !important;
}
.navhi {
	color: #00FF00 !important;
	text-decoration: underline !important;
}

#MyHomeMenu {
	font-family: 'Trebuchet MS', 'Lucida Grande',
	  Verdana, Lucida, Geneva, Helvetica, 
	  Arial, sans-serif !important;
	font-size: 15px !important;
	color: #FFFFFF !important;
	border: 1px solid #AAA !important;
	}

#MyHomeMenu ul {
	display: inline !important;
	list-style: none !important;
	margin: 0 !important;
	padding: 0 !important;
	border: none !important;
	}
		
#MyHomeMenu li {
	display: inline !important;
	padding: 0px 15px !important;
	border-left: 1px solid #BBB !important;
	margin: 0 !important;
	}
	
#MyHomeMenu li.first {
	border-left: none !important;
	margin: 0 !important;
	}
	
#MyHomeMenu a {
	text-decoration: underline !important;
	color: #FFF !important;
	font-size: 15px !important;
	}

#MyHomeMenu li:hover, #menunav li.over{
	background-color: #777777 !important;
	color: orange !important;
	font-weight: bold !important;
	}

#MyHomeMenu a:hover {
	background-color: #777777 !important;
	color: orange !important;
	font-weight: bold !important;
	}

pre {
    font-family: monospace !important;
    font-size: 9pt !important;
    }

font.mono {
    font-family: courier,  monospace !important;
    font-size: 9pt !important;
    }

font.small {
    font-size: 7pt !important;
}

.form
{
	border: 1px solid #CC0000 !important;
	background-color: #000000 !important;
	color: white !important;
}

#success
{
	background: red !important;
	color: white !important;
	font-weight: bold !important;
	font-size: 11px !important;
	position: absolute !important;
	top: 0 !important;
	left: 0 !important;
	height: 15px !important;
	width: 150px !important;
}

.error
{
	font-size: 11px !important;
	color: red !important;
	font-weight: bold !important;
	padding: 5px 0px 5px 0px !important;
}

.effect
{
	color: red !important;
	font-weight: bold !important;
	text-decoration: underline !important;
}

#tutorial
{
	font-size: 130% !important;
}

#tutorial b
{
	font-size: 140% !important;
}

.formField { font-size: 11px !important; font-family:Verdana, Arial !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; }
.formFieldPt9 { font-size: 11px !important; font-family:Verdana, Arial !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; font-size: 9pt !important; }

.tableBackgroundColor  {background-color: #000000 !important;}
.tableBackgroundColor2 {background-color: #181818 !important;}

}
