css:
@namespace  url(http://www.w3.org/1999/xhtml);
/*
 * Drupal Redesigned
 *
 * Author      : Juan J Gonzalez Cardenas - MyXelf
 * Version     : 1.0 (26.Nov.2010)
 * Description : Drupal Web site with the redesign but heavily customized
 */

@-moz-document url-prefix("http://drupal.org"),
               url-prefix("https://drupal.org"),
               domain("api.drupal.org"),
               domain("localize.drupal.org") {

/* General */
body, caption, th, td, input, textarea, select, option, legend, fieldset { font-family: Tahoma, Verdana, Arial, sans-serif !important; line-height: 1.5em !important; }

.container-12 { width: auto !important; }
.grid-12 { width: 98% !important; margin-left: 1% !important; margin-right: 1% !important; }
.grid-8 { width: 64% !important; }
.grid-7 { width: 57% !important; }
.grid-6 { width: 48% !important; }
.grid-5 { width: 39% !important; }
.grid-4 { width: 31% !important; }
.grid-footer { width: 18% !important; }

#header .grid-6 { width: 85% !important; }
#homebox.column-count-3 .homebox-column-wrapper { width: 31% !important; }

.alpha { margin-left: 0 !important; }
.omega { margin-right: 0 !important; }
.marker { margin-left: 5px !important; }

.tips { color: #666666; margin-left: 2em !important; }

/* Tags */
h1, .h1 { color: #222 !important; }
h1, h2, h3, h4, .h1, .h2, .h3, .h4 { font-family: Tahoma, Arial, sans-serif !important; }
h2, .h2, #aggregator .feed-item-title { font-size: 1.8em !important; }
h3, .h3, #profile .name { color: #999 !important; font-size: 1.4em !important; font-weight: bold !important; }
h4, .h4, h5, .h5, h6, .h6 { color: #333 !important; line-height: 1.5em !important; }
a.link-button { font-family: Tahoma, Arial, sans-serif !important; }
.profile .item-list li, #column-right .block .item-list li, #homebox .block .item-list li { line-height: 1.5em !important; }
.item-list li strong { padding-bottom: 15px !important; }
input:not([type="image"]), textarea, select { border: 1px solid #CCC !important; font-size: 1em !important; padding: 1px 5px !important; }
fieldset { border: 1px solid #CCC !important; margin: 0.75em 0 !important; padding: 10px !important; }
dt { margin-top: 1em !important; }

/* Header */
#header { background: #FFF url("data:image/png;base64,") 0 0 repeat-x !important; height: 74px !important; }
#header-inner { float: right; margin-right: 10px !important; }
#header-content { position: absolute !important; left: 0!important; height: 84px !important; }
#header-right { float: right !important; }
#header-right-inner { margin-top: 30px !important; }

/* Druplicon Forever! */
#site-name a { background: url("data:image/png;base64,") 0 0 no-repeat !important; width: 220px !important; height: 84px !important; }

/* Header - Navigators */
#nav-header, #nav-masthead { width: auto !important; z-index: 3 !important; margin: 0 !important; }
#nav-header ul li a:hover, #nav-header ul li a:focus,
#nav-masthead ul li.active a, #nav-masthead ul li:hover a, #nav-masthead ul li:focus a { color: #FFFF00 !important; }
#nav-masthead ul { float: left !important; }
#nav-masthead li, #nav-masthead li a {  background: none !important; }
#nav-masthead li { padding: 0 !important; }
#nav-masthead a { padding: 0.333333em 1em !important; }

#nav-content ul li { padding: 6px 12px 4px !important; }
#nav-content li.active a.active, #nav-content li.active-parent a { padding: 0.507692em 0.692307em 0.646153em 0.230769em !important; }
#nav-content li.active, #nav-content li.active-parent { padding: 0 0 0 0.384615em !important; }

/* Header - UserInfo */
#userinfo { margin: 0 !important; }
#userinfo a { float: left; margin: 0 !important; }
#userinfo a[href='http://drupal.org/user'] { color: #0000FF !important; }
#userinfo a:hover, #userinfo a:hover { color: #FFFF00 !important; }

/* Breadcrumbs */
.breadcrumb { color: #999 !important; padding-bottom: 1em !important; }
.breadcrumb a, .breadcrumb a:visited { color: #999 !important; }

/* Front Page */
body.drupalorg-front #header { height: 20em !important; }
body.drupalorg-front #header-left h2 { color: #555 !important; margin-top: 24px !important; }

.drupalorg-front #page { margin-top: 0.5em !important; }
.drupalorg-front #header-right-inner { margin-top: 85px !important; }
.drupalorg-front #edit-search-theme-form-1-wrapper label { color: #000 !important; }
.drupalorg-front #search-theme-form-advanced legend { color: #666 !important; padding: 5px 0 !important; }
.drupalorg-front #search-theme-form-advanced .form-radios label { color: #000 !important; }

.front-current-activity { font-size: 1.1em !important; }
.front #content .block-content ul { font-size: 1.1em !important; line-height: 1.6em !important; }

/* Search Widget */
#edit-search-theme-form-1 {  background-position: 4px -863px !important; font-size: 9px !important; min-height: 14px !important; border-width: 1px !important; margin-top: 1px !important; padding: 2px 10px 2px 28px !important; width: 235px !important; }
#search-theme-form-submit { height: 22px !important; margin-top: 0 !important; }
#search-theme-form-advanced { margin-top: -5px !important; }
#search-theme-form-advanced fieldset { border: 0 none !important; margin: 0 !important; padding: 0 !important; }
#search-theme-form-advanced fieldset.collapsible legend { font-size: 9px !important; top: 1px !important; }

/* Blocks */
#column-right .block { border: 1px solid #7F98A7 !important; margin-bottom: 15px !important; -moz-border-radius: 5px; -webkit-border-radius: 5px; }
.block h2 {  border-bottom: 1px solid #DDD !important; font-size: 1.7em !important; margin-bottom: 0 !important; padding: 1px 7px !important; }
.block .block-inner { padding: 0 !important; }
.block .block-content { padding: 7px 7px 10px !important; }

.page-search #column-right .block { background-color: #F6F6F2 !important; }

/* Tabs */
#tabs { margin-bottom: 10px !important; }
#tabs ul.primary li a { background-color: #EEE !important; text-align: center; min-width: 4em; -moz-border-radius: 3px; -webkit-border-radius: 3px; }
#tabs ul.primary li.active a { background-color: #96BC44 !important; }
#tabs ul.tabs li a:hover, #tabs ul.tabs li a:focus { color: #333 !important; }

/* Code */
div.codeblock, pre { background-color: #FEFEC9 !important; border-color: #FFEBC5 !important; padding: 8px 10px !important; }
code, tt, pre { font-family: "DejaVu Sans Mono",Monaco, "Lucida Console", monospace !important; background-color: #FEFEC9 !important; color: #333333; }

/* Comments */
#content .comment { border: 1px solid #7F98A7 !important; -moz-border-radius: 5px; -webkit-border-radius: 5px; background-color: #F6F6F2 !important; margin-bottom: 1em !important; padding: 0.2em 1.5em !important; }
#content .comment h3.comment-title { font-weight: normal !important; margin-bottom: 0.375em !important; }
#content .comment .links { font-weight: bold !important; }

/* Tables */
.sticky-header { z-index: 10; }

/* Footer */
#footer-region li { margin: 5px 0 !important; }
#footer-region li.first a { font-weight: bold; }
#footer-message a { color: #666 !important; font-weight: bold; }

/* Portlets */
#homebox .homebox-portlet-inner { border: 1px solid #7F98A7 !important; -moz-border-radius: 5px; -webkit-border-radius: 5px; }
#homebox .comment-info a { margin-left: 5px !important; }

/* Project & Tracker & Issues */
#block-project_issue-issue_cockpit .category-header, #homebox-block-project_issue_issue_cockpit .category-header { font-family: Tahoma, Arial, sans-serif !important; }
.project-issue .header { font-size: 1.1em !important; padding-bottom: 0.5em !important; }
.project-issue .summary table { background-color: #FEFEC9 !important; margin-bottom: 1em !important; }
.project-issue .summary table td { padding: 0.1em 1em !important; }
#project-issue-summary-links h3 { font-size: 1.1em !important; font-weight: bold !important; }
#project-issue-summary-links ul.internal-links li { margin: 0.5em 0 !important; }
#project-issue-summary-links ul.internal-links li:before { content: "â†“"; margin-right: 0.5em; }

#tracker th, #tracker td, .project-issue th, .project-issue td { padding: 0.6em 0.5em !important; }
#tracker td.replies { text-align: left !important; }
#tracker .replies br, .project-issue td.views-field-comment-count br { display: none !important; }
#tracker .replies a, .project-issue td.views-field-comment-count a { margin-left: 3px !important; white-space: nowrap !important; }
#tracker .replies a:before, .project-issue td.views-field-comment-count a:before { content: '('; }
#tracker .replies a:after, .project-issue td.views-field-comment-count a:after { content: ')'; }
.project-issue td.views-field-category { white-space: nowrap !important; }

/* Community Search Help */
#drupalorg-search-help-form { padding: 10px 10px 5px !important; }
#drupalorg-search-help-form label { font-size: 1.4em !important; font-weight: bold !important; }
#drupalorg-search-help-form .form-text { height: 21px !important; font-size: 12px !important; background-position: 98% 1px !important; width: 90% !important; } 
#drupalorg-search-help-form .form-text.throbbing { background-position: 98% -99px !important; }

}
