css:
#iphone_banner, [src*='.keewurd.'], [src*='.liftdna.'] {position:absolute!important; visibility:hidden!important; display:none!important; z-index:-1000000!important;}

html, body {overflow-x:hidden!important; height:100%!important;}

.directory .right_col.ad, .ad_placeholder, .footer_ad, #footer, #adaptvcompanion_wrapper, #ad_holder, #banner_default, #banner_custom, #share_tab, #report_tab, #share_all > a, #chat_settings_switch_type, .footer_ad, #PopUnderDir_holder, .managed_ad, body[class*='channel'] .left_col_bottom, body[class*='channel'] #footer, #banner_custom, #right_column.ad, #picture_links, .custom .title, #channel_description_static, #channel_stats, body[class*='channel'] #related > h3.chan_sect_title, .admin-subadmin-and-promoter-only, #ad_iframe, #popout_message, #adaptv_ad_player_div, .header_notification, .new_alert
{
display:none!important; position:absolute!important; top:-10000px!important; z-index:-10000000000!important; visibility:hidden!important; border:0!important; width:0!important; height:0!important;
}

.directory .left_col {width:100%!important; margin-top:-35px!important;}

body[class*='channel'] #channels_stem, body[class*='channel'] .dropmenu_links_container {margin-left:99px!important; margin-top:1px!important;}
body[class*='channel'] #channels_stem .main span {margin-top:2px!important; line-height:14px!important;}

body[class*='channel'] .wrapper {margin-top:0!important;}
body[class*='channel'] #admin_nxtchan > a {position:absolute; left:-128px!important; width:94px!important;}

[class*='channel'] #standard_holder {top:0!important; left:0; margin-right:0!important; margin-bottom:18px!important; min-width:635px!important; max-width:100%!important; height:354px!important;}

body:not([class*='channel']) .live_site_player_container.swf_container#standard_holder {width:100%!important;height:100%!important;}

[class*='channel'] #standard_holder embed {position:relative; top:1px!important;}

body[class*='channel'] #left_col > div {padding-right:0!important; padding-bottom:0!important; padding-left:0!important; height:561px!important;}

body[class*='channel'] #share_api_buttons_dtabs {top:44px;}
body[class*='channel'] #facebook_like_button {position:relative; top:0px!important;}

body[class*='channel'] #info {padding-left:5px!important; margin-top:0px!important; margin-bottom:5px!important;}

#share_contents {height:51px!important;}
#stats {position:relative; top:75px; left:210px; margin:5px 0!important; margin-top:-12px!important;}
#stats h3 {top:5px;}
#popped {position:relative; top:-1px; left:-215px; font-size:17px!important; z-index:10!important;}
#popped img {position:relative; top:-2px;}
#actions > ul {height:0!important; padding:0!important; border:0!important;}
#favorite_tab {position:relative; top:43px; left:107px!important; z-index:10!important;}
#favorite_tab > span {position:relative; top:6px;}

body[class*='channel'] #actions {width:635px!important; margin-right:0!important; border:0!important;}
body[class*='channel'] #action_contents {position:relative; top:-34px!important;}

body[class*='channel'] .action_share_top {position:relative; top:30px!important; padding-left:50px!important;}
body[class*='channel'] .action_share_top > div[style="font-size:12px"] {display:none!important;}

body[class*='channel'] .action_share_bottom {position:relative; left:0px!important; top:-2px!important;}
body[class*='channel'] .action_share_bottom > div[style="font-size:12px"] {display:none!important;}

body[class*='channel'] #right_col > div:first-child {height:0!important;}
body[class*='channel'] #chat_container {position:relative; left:0; top:566px; left:-640px!important; padding-top:0!important; padding-right:0!important; padding-left:0!important; width:965px!important; webkit-appearance:none!important; border-top-right-radius:8px!important; z-index:2;}
body[class*='channel'] #chat_container #chat_menu_buttons {position:absolute; left:0; top:0px; -webkit-transform:rotate(-90deg); z-index:5!important;}
body[class*='channel'] #chat_container #chat_menu_buttons > div {position:absolute; left:0; display:inline!important;}
body[class*='channel'] #chat_container #chat_menu_buttons > #chat_settings_dropmenu_button {top:50px; left:0;}
body[class*='channel'] #chat_container #chat_viewers_dropmenu {left:634px; top:-0px!important; width:312px!important;}
body[class*='channel'] #chat_container #viewer_list {padding:0!important; width:320px!important; height:411px!important;}
body[class*='channel'] #chat_container #viewer_list h3.drag_handle {width:311px!important; padding:5px!important; margin:0!important; position:relative; left:0px!important; top:0px!important;}
body[class*='channel'] #chat_container #viewer_list > ul#viewers {max-height:100%!important;}
body[class*='channel'] #chat_container #viewers h4 {border-top-width:0!important; padding-top:0!important;}
body[class*='channel'] #chat_container #viewers .viewer_list {margin-bottom:0!important;}

body[class*='channel'] #chat_settings_dropmenu {position:absolute; top:0; left:634px; width:321px!important;}
body[class*='channel'] #chat_credentials {border-bottom-width:0!important;}
body[class*='channel'] #chat_container #chat_help > div {padding:5px!important; margin-top:0!important;}
body[class*='channel'] #jtv_settings {padding:5px 10px!important;}

body[class*='channel'] #chat_lines {margin-bottom:0!important;}
body[class*='channel'] #speak .td_pic img {margin-right:0!important;}

body[class*='channel'] #related {position:relative; top:-22px; left:-5px; padding-top:0px!important; padding-bottom:0px!important;padding-right:0!important; padding-left:6px!important; z-index:1; width:324px!important;}
body[class*='channel'] #related_list {height:567px!important;}
body[class*='channel'] #related li[class='list_item channel horiz-1_col clearfix'] { }
body[class*='channel'] #related li[class='list_item channel horiz-1_col clearfix']:last-child {border:0!important;}

body[class*='channel'] #speak #speak_options {padding-right:5px!important;}
