css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("koenews.blog9.fc2.com") {

body{
	font-family:"メイリオ","Meiryo",sans-serif !important;
}
#outline{
	/*background-color:transparent!important;*/
	margin-top:-30px !important;
	width:1170px!important;
	overflow:hidden!important;
}

/*TOP絵*/
div#header_outline{
	display: none!important;
}

/*トップメニュー*/
ul#menu_bar{
	border-top:3px solid #EFE0FF !important;
}
ul#menu_bar li a{
	width:167px!important;
}
/*メールフォーム*/
ul#menu_bar li:nth-of-type(7) a{
	width:161px!important;
}
ul#menu_bar li form#cse-search-box div input[name="q"]{
	width:280px!important;
}
html body div#outline div#header_outline div#header_body h1{
	text-align:center !important;
}

/*他のページへのリンク*/
.rss1
,.rss2
,.afi{
	display:none !important;
}

/*削除*/
/*上部ブログバー*/
#sh_fc2blogheadbar,
/*#header_body,*/
div.pagerank table tr:nth-of-type(2),
div.pagerank table tr:nth-last-of-type(1),
div.pagerank table tr:nth-last-of-type(2),
div.pagerank table tr:nth-last-of-type(3),
div.afii,

/*amazon*/
/*.plugin1_outline:nth-of-type(4),*/

/*お世話になってるサイト*/
.plugin1_outline:nth-of-type(5),

/*top絵ありがとうございます*/
.plugin1_outline:nth-of-type(6),

/*月別アーカイブ*/
/*.plugin1_outline:nth-of-type(7)*/

/*はてなブックマーク*/
/*.plugin1_outline:nth-of-type(8),*/

/*リンク*/
.plugin1_outline:nth-of-type(9)

/*カウンタ*/
/*.plugin1_outline:nth-of-type(10)*/
/*謎の吹き出しマーク*/
,img#sm-target-image
{
	display:none !important;
}

.plugin1_outline .plugin1_body ul li a:hover,
.plugin1_outline .ta_left ul li a:hover{
	text-decoration:underline !important;
}

/*上部リンク*/
#menu_bar a:visited
,#menu_bar a:hover{
	color:#FFFFFF !important;
}
/*リンク指定*/
a:visited{
	color:#F09199!important;
	color:#FF8FAB!important;
}
a:hover
,.plugin1_outline:nth-of-type(1) .plugin1_body ul li a:hover
,.plugin1_outline:nth-of-type(1) .ta_left ul li a:hover
/*最新記事ホバー*/
{
	color:#FF9FD0!important;
	color:#FF8D4F!important;
	color:#FF7FCC!important;

	text-decoration:none!important;
}

/*左カラム*/
#center-left{
	position:relative!important;
}
/*最新記事*/
.plugin1_outline:nth-of-type(1){
	/*position:relative!important;
	top:0px!important;
	left:0px!important;*/
}

div#left div.plugin1_outline div.plugin1_body ul{
	padding-bottom:20px!important;
}

/*はてなブックマーク*/
.plugin1_outline:nth-of-type(8) .plugin1_body{
	padding-right:6px!important;
	padding-left:6px!important;
}
/*新着エントリー*/
.hatena-bookmark-widget-title a:hover{
	color:#DFDFDF!important;
	text-decoration:none!important;
}
/*内容*/
.plugin1_outline .plugin1_body .hatena-bookmark-widget-body a:hover,
.plugin1_outline .hatena-bookmark-widget-body a:hover{
	color:#6A68EF!important;
	text-decoration:none!important;
}

#center-left{
	margin-bottom:30px!important;
}

/*-------------amazon------------------------------------------------*/
.plugin1_outline:nth-of-type(4){
	display: none!important;
}

/*記事終りのamazon*/
html body div#outline div#center-left div#center div.ently_outline div.ently_body div.ently_text div.amazon table tbody tr td:first-child a img{
	border: 0px solid #E95388!important;
}
html body div#outline div#center-left div#center div.ently_outline div.ently_body div.ently_text div.amazon table tbody tr td:first-child a img:hover
,html body div#outline div#center-left div#center div.ently_outline div.ently_body div.ently_text div.amazon table tbody tr td font a img:hover
,html body div#outline div#center-left div#center div.ently_outline div.ently_navi a img:hover
,div.amazon img:hover{
	opacity:0.7 !important;
}

/*人気ページランキング*/
/*タイトル*/
div.pagerank .pagerank_title{
	font-size:12px !important;
	color:#E95388!important;
	text-align:left !important;
	padding-bottom:4px!important;

	background-color:transparent!important;
	border-bottom: 2px solid #E95388!important;

}

/*ランキング*/
div.pagerank{
	width:212px!important;
	margin-left:8px!important;	
	border:0px solid #F4B3C2!important;


	position:absolute!important;
	/*top:1720px!important;*/
	/*bottom:85px!important;*/
	top:0px!important;
	left:935px!important;

	z-index:1!important;
}
div.pagerank div[align="center"]{
	width:218px!important;
	z-index:10!important;
}
div.pagerank div[align="center"] div[style="height: 400px; width: 650px; overflow: auto;"]{
	width:218px!important;
}

#acrp_koenew
,#acrp_koenew table{
	width:210px!important;
	padding-right:2px!important;
	position:absolute!important;
	overflow:hidden!important;
	overflow:visible!important;
}
div.pagerank a{
	font-size:11px !important;
	-webkit-font-size:10px !important;
}
div.pagerank table td{
	font-size:11px !important;
	padding-top:10px!important;
	padding-left:10px!important;
	padding-bottom:5px!important;

	border-bottom: 1px dotted #FFC6FF!important;
}
div.pagerank table tr>td[align="center"]{
	font-size:11px !important;
	color:#7F7F7F!important;
}

/*ページの文字を消す*/
div.pagerank table tr:nth-of-type(1) td{
	color:#FFF9FC!important;
	line-height:0!important;

	padding-top:5px!important;
	padding-bottom:0px!important;
	border-bottom:0px solid #FFC6FF!important;

}
/*ランキングの左側の番号*/
div.pagerank table tr td:nth-of-type(1)
/*ランキング内のPR*/
,div.pagerank table tr:nth-of-type(1)
,div.pagerank table tr:nth-last-of-type(3){
	display:none !important;
}
/*人気ページランキング全体の高さ*/
div.pagerank>div div{
	height:1500px!important;
}

/*------------ナビゲーション------------------------------------------*/
.page_navi{
	width:200%!important;
	height:60px!important;
	margin-top:30px!important;

	position:relative!important;
	top:-5px!important;
	left:-600px!important;
}
.page_navi a{
	background-color:#FFF4FF !important;
	border: 1px solid #FFF4FF !important;
	overflow:hidden !important;
	color:#EF688A!important;
	-moz-border-radius:10px 10px 10px 10px !important;
	-webkit-border-radius:10px 10px 10px 10px !important;
	-o-border-radius:10px 10px 10px 10px !important;
}
.page_navi a:hover{
	background-color:#FFE0EF !important;
}

/*navi(bottom)*/

/*new page*/
.page_navi a:first-child{
	width:55%!important;
	position:absolute!important;
	top:0px!important;
	left:545px!important;
}

/*HOME*/
.page_navi a:nth-of-type(2){
	width:55%!important;
	position:absolute!important;
	top:25px!important;
	left:545px!important;
}
.page_navi a[href="http://koenews.blog9.fc2.com/"]{
	color:#FF9FD0!important;
}
/*old page*/
.page_navi a:nth-of-type(3){
	width:55%!important;
	position:absolute!important;
	top:50px!important;
	left:545px!important;
}
textarea{
	width:90%!important;
	overflow:auto!important;
}

