css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.yahoo.co.jp"),
url-prefix("http://headlines.yahoo.co.jp"),
url-prefix("http://dailynews.yahoo.co.jp"),
url-prefix("http://zasshi.news.yahoo.co.jp"),
url-prefix("http://opinions.news.yahoo.co.jp"),
url-prefix("http://search.yahoo.co.jp"),
url-prefix("http://detail.chiebukuro.yahoo.co.jp/"),
url-prefix("http://backnumber.dailynews.yahoo.co.jp/")
{

body{
	font-family:"メイリオ","Meiryo",sans-serif !important;
}

#selectionR,
#brandpanel,
#commerce,
#centralPosition,
#cmpIE8,
#tct,
#chie-ams,
.adCt{
	display:none !important;
}

/*右カラム広告削除*/
table tbody tr td[style="width:300px; padding:0px 0px 0px 0px; vertical-align:top;"]>table:nth-of-type(1),
/*table tbody tr td:nth-of-type(2)>table:nth-of-type(1),*/

table tbody tr td[style="width:300px; padding:0px 0px 0px 0px; vertical-align:top;"]>table:nth-of-type(4)+div,

#pos-lrec,
#pos-tn,
.ymuiHeaderBGLG,.ymui3DHeaderLG,
.adTyumoku,
#sub .marB10:nth-of-type(1)+div,
.adRp,
#pos-sqb,
#adLrec,
#adSqm,
#rightbox.bx,
#rightbox1,
#rightbox2,
#rightbox2+table+div.ynbox_side,
#adRp,
#center1,
#center1+div,
#center2,
#adSqb,
div[style="margin-bottom: 10px;"],
div[style="margin: 5px 0pt 10px;"],
#trendOthers+div,
#adAms,
/*バナー*/
#composite
{
	display:none !important;
}
/*リンク*/
a:link{
	text-decoration:none!important;
}
a:visited{
	
	text-decoration:none!important;
}
a:hover{
	color:#FF8D4F!important;
	text-decoration:none!important;
}
a img:hover{
	opacity:0.8;
}
#gnPri a{
	text-decoration:none!important;
}
#gnPri a:hover{
	color:#AFAFAF!important;
	text-decoration:none!important;
}

#gnSec a{
	text-decoration:none!important;
}
#gnSec a:visited{
	color:#AFAFAF!important;
	text-decoration:none!important;
}
#gnSec a:hover{
	color:#3F3F3F!important;
	text-decoration:none!important;
}
#gnSec li.on a{
	color:#FFFFFF!important;
	text-decoration:none!important;
}

div#SaA.cf div#SaA-tg strong.yschon
,div#SaC div#SaC-tg.cf div#SaC-tg-inner strong.yschon
,div#SaC div#SaC-tg strong.yschon{
	color:#118F5D!important;
}
}

/*----------------------------------------------------------------------------*/
@-moz-document domain("www.yahoo.co.jp"){

a:link{
	text-decoration:underline!important;
	text-decoration:none!important;
}
a:hover{
	color:#FF8D4F!important;
	text-decoration:none!important;
}
a img:hover
,.imgfilter:hover{
	opacity:0.8;
}
div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab0.on span a#search
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab1.on span a#isearch
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab2.on span a#msearch
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab3.on span a#dsearch
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab4.on span a#ksearch
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab5.on span a#csearch
,div#searchbox div#srchb form fieldset div#srchbd ul.tab li.tab6.on span a#rtsearch
{
	color:#118F5D!important;
}
div#topicsbox.bx div.hd ul.tab li.tab0.on span a#topics
,div#topicsbox.bx div.hd ul.tab li.tab1.on span a#economy
,div#topicsbox.bx div.hd ul.tab li.tab2.on span a#entertainment
,div#topicsbox.bx div.hd ul.tab li.tab3.on span a#sports
,div#topicsbox.bx div.hd ul.tab li.tab4.on span a#others{
	color:#118F5D!important;
}
}

/*----------------------------リアルタイム検索など--------------------------------*/
