css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com"){

.component #custom_49
,iframe#j_ad{
	display:none !important;
}

/*--------------------ホーム------------------------------*/
/*自分の名前*/
div#page-node-home div.dashboard div.module div.flex-module a.account-summary div.content div.account-group b.fullname{
	font-weight:normal !important;
}
/*数字*/
div#page-container.wrapper div#page-node-home div.dashboard div.module div.js-mini-profile-stats-container ul.stats li a strong{
	color:#4F4F4F!important;
	font-weight:normal !important;
}
/*ツイートエリア*/
div#page-node-home div.dashboard div.module div.tweet-box div.tweet-box div.text-area div.text-area-editor textarea.twitter-anywhere-tweet-box-editor{
	color:#4F4F4F!important;
}
/*フロートのツイートエリア*/
div#tweet_dialog.twttr-dialog-container div.twttr-dialog div.twttr-dialog-header h3{
	color:#5F5F5F !important;
	font-weight:normal !important;
}
/*おすすめユーザー*/
html.js body.t1 div#doc.route-home div#page-outer div#page-container.wrapper div#page-node-home div.dashboard div.component div.module div.flex-module div.js-recommended-followers div.js-account-summary div.content a.account-group span.account-group-inner b.fullname{
	font-weight:normal !important;
	color:#4F4F4F!important;
}
/*------------------------自分のページ----------------------*/
/*名前*/
div#page-container.wrapper div div.js-content-top div.module div.flex-module div.profile-card-inner h1.fullname{
	font-size:24px !important;
	color:#4F4F4F!important;
	font-weight:normal !important;
	margin-bottom:5px !important;
}
.bio{
	color:#666666 !important;
	font-family:"メイリオ","Meiryo",sans-serif !important;
	font-style:normal!important;
	font-size:13px !important;
	line-height:17px !important;
	white-space:pre-wrap !important;
}

/*------------------------プロフィールを更新---------------------------------*/
div#page-container.wrapper div div.js-content-top div.module div.flex-module div.profile-card-actions div.follow-btn-container a.btn[href="/settings/profile"]
,div#page-container.wrapper div div.js-content-top div.module div.flex-module div.profile-card-actions ul.stats li a strong{
	color:#5F5F5F!important;
	margin-right:2px !important;
}
/*名前*/
div#page-container.wrapper div.dashboard div.module div.flex-module a.account-summary div.content div.account-group b.fullname{
	font-weight:normal !important;
}
/*各項目*/
html body.t1 div#doc div#page-outer div#page-container.wrapper div.dashboard div.module ul li.active a.list-link{
	font-weight:normal !important;
}
/*--------------------ユーザー情報------------------------------*/
html body.t1 div#doc div#page-outer div#page-container.wrapper div.content-main div.content-header div.header-inner h2{
	font-weight:normal !important;
	color:#4F4F4F!important;	
}
/*入力文字色*/
fieldset.control-group div.controls input
,form#account-form.form-horizontal fieldset.control-group div.controls select{
	color:#4F4F4F!important;
}
fieldset.control-group div.controls input:focus
,form#account-form.form-horizontal fieldset.control-group div.controls select:focus{
	color:#6F6F6F!important;
}
/*すべての位置情報を削除*/
form#account-form.form-horizontal fieldset.control-group div.controls div#delete_geo_data p a#geo_button.delete-info{
	color:#5F5F5F!important;
}

/*----------------------------通知機能----------------------*/
form#notifications-form.form-horizontal fieldset.control-group h3.settings-header{
	font-weight:normal !important;
	color:#4F4F4F!important;	
}

/*-----------------------プロフィール----------------------*/
/*プロフィール文字*/
h2{
	font-size:14px !important;
	margin-bottom:2px !important;
}
/*入力文字色*/
div#page-container.wrapper div.content-main div.content-inner form#profile-form.form-horizontal fieldset.control-group div.controls input
,form#profile-form.form-horizontal fieldset.control-group div.controls textarea{
	color:#4F4F4F!important;
}
div#page-container.wrapper div.content-main div.content-inner form#profile-form.form-horizontal fieldset.control-group div.controls input:focus
,form#profile-form.form-horizontal fieldset.control-group div.controls textarea:focus{
	color:#6F6F6F!important;
}

form#profile-form.form-horizontal fieldset.control-group{
	margin-left:-2em !important;
}
form#profile-form.form-horizontal fieldset#profile-image-controls
,form#profile-form.form-horizontal fieldset.form-actions{
	margin-left:2em !important;
}
/*自己紹介欄*/
textarea#user_description{
	height:12em!important;
	width:29em!important;
	overflow:auto;
}
/*facebookに表示する*/
form#profile-form.form-horizontal div#fb-button.fb-button-or-iframe div.controls a#fb-anchor.btn span{
	color:#5F5F5F!important;
}

/*-----------------------デザイン----------------------*/
form#design-form.form-horizontal h3.settings-header{
	font-weight:normal !important;
	color:#4F4F4F!important;
}

/*-----------------------アプリ連携----------------------*/
div.stream-item div.app div.content div strong{
	font-weight:normal !important;
}
html body.t1 div#doc div#page-outer div#page-container.wrapper div.content-main div#apps-stream.stream div.stream-item div.app a[type="button"]{
	font-weight:normal !important;
	color:#5F5F5F!important;
}

/*--------------------------------------------------------------------------------*/


/*------------------------自分のページ左カラム---------------------------------*/
 div#page-container.wrapper div div.dashboard div.component div.module ul.js-items-container li.js-sidenav-link a.list-link{
	font-weight:normal !important;
}
/*------------------------------ツイート------------------------*/
div#page-outer div#page-container.wrapper div div.content-main div.content-header div.header-inner h2 span.js-stream-title{
	font-weight:normal !important;
}
/*ツイート者名*/
div#stream-items-id.js-stream-items div.js-stream-item div.tweet div.content div.stream-item-header a.account-group strong.fullname{
	font-weight:normal !important;
	/*color:#5F5F5F!important;*/
}
/*ツイート*/
div#stream-items-id.js-stream-items div.js-stream-item div.tweet div.content p.js-tweet-text{
	color:#4F4F4F!important;

	font-size:14px !important;
	line-height:19px !important;
	white-space:pre-wrap !important;
	cursor:text;
}
/*投稿時間*/
.tweet-timestamp:after{
	content: "　"attr(title)"" !important;
}

/*------------------------------フォロー------------------------*/
/*名前*/
div#stream-items-id.js-stream-items div.js-stream-item div.account div.content div.stream-item-header a.account-group strong.fullname{
	font-weight:normal !important;
}
/*フロート時プロフィール・ツイート*/
div#mini-profile.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.expando-profile-popup div.component div.profile-modal div.profile-modal-header div.profile-modal-header-inner h2.fullname a

,div#mini-profile.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.expando-profile-popup div.component div.profile-modal ul.stats li a strong

,div#mini-profile.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.expando-profile-popup div.component div.profile-modal div.mini-profile-footer div.follow-bar div.js-follow-combo a.follow-btn div.js-action-follow

,div#mini-profile.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.expando-profile-popup div.component div.recent-tweets div.simple-tweet div.content div.stream-item-header a.account-group strong.fullname{
	font-weight:normal !important;
}
/*投稿時間*/
div#mini-profile.twttr-dialog-container .tweet-timestamp:after{
	content: "" !important;
}

/*------------------------------フォロワー------------------------*/
/*フォローボタン*/
div#stream-items-id.js-stream-items div.js-stream-item div.account div.js-follow-combo a.follow-btn div.js-action-follow{
	color:#4F4F4F!important;
}
/*------------------------------リスト------------------------*/
/*保存している/保存されている*/
div#page-container.wrapper div div.content-main div.content-header div.header-inner h2 small.view-toggler{
	margin-left:2em !important;
}
div#page-container.wrapper div div.content-main div.content-header div.header-inner h2 small.view-toggler a{
	font-weight:normal !important;
}
/*リストを作成する*/
div#page-outer div#page-container.wrapper div div.content-main div.content-header div.header-inner h2 span.content-header-buttons a.btn,
/*リスト名*/
div#stream-items-id.js-stream-items div.js-stream-item div.list div.content div.stream-item-header a.js-list-link strong{
	font-weight:normal !important;
}

/*---------------------------リストページ------------------------------*/
/*リスト名*/
html.js body.t1 div#doc.route-list div#page-outer div#page-container.wrapper div div.dashboard div.module div.flex-module div.flex-module-header h1.js-list-name{
	font-size:18px !important;
	font-weight:normal !important;
	color:#4F4F4F!important;
}
/*数字*/
html.js body.t1 div#doc.route-list div#page-outer div#page-container.wrapper div div.dashboard div.module ul.stats li a strong{
	color:#4F4F4F!important;
	font-weight:normal !important;
}
/*編集*/
div#page-container.wrapper div div.dashboard div.module div.follow-card-footer div.follow-bar a.btn

,html.js body.t1 div.twttr-dialog-wrapper div.twttr-dialog-container div.twttr-dialog div.twttr-dialog-header h3
,html.js body.t1 div.twttr-dialog-wrapper div.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.list-editor div.list-editor-save div.button{
	color:#4F4F4F!important;
	font-weight:normal !important;
}
/*削除時の「はい・いいえ」ボタン*/
html.js body.t1 div.twttr-dialog-wrapper div.twttr-dialog-container div.twttr-dialog div.twttr-dialog-inside div.twttr-dialog-body div.twttr-dialog-content div.twttr-prompt div.btn{
	font-weight:normal !important;
}
/*最近追加されたユーザー・～さんのリスト*/
html.js body.t1 div#doc.route-list div#page-outer div#page-container.wrapper div div.dashboard div.component div.module div.flex-module div.flex-module-header h3{
	color:#4F4F4F!important;
}
/*------------------------似ているユーザー---------------------------*/
/*名前*/
div#page-container.wrapper div div.dashboard div.component div.module div.flex-module div.flex-module-inner div.js-account-summary div.content a.account-group span.account-group-inner b.fullname{
	font-weight:normal !important;	
}


/*------------------------------＠つながり-------------------------------------*/
/*ユーザー名*/
div#stream-items-id.js-stream-items div.js-stream-item div.stream-item-content div.stream-item-activity-header div.stream-item-activity-line a.pretty-link strong.fullname{
	font-weight:normal !important;	
}

/*------------------------------＃みつける----------------------------------------*/
/*最近の話題*/
html.js body.t1 div#doc.route-discover div#page-outer div#page-container.wrapper div div.content-main div.stream div#discover-stream-manager.js-stream-manager div.stream-container div.stream div#stream-items-id.js-stream-items div.js-stream-item div.story span.story-header h3.story-title a.js-action-card-search{
	font-weight:normal !important;		
}
/*おすすめユーザー*/
div#page-container.wrapper div div.content-main div.content-header div.component form.js-search-form table.flex-table tbody tr td.flex-table-secondary button.btn{
	font-weight:normal !important;	
}
/*友だちを見つける*/
div#stream-items-id.js-stream-items div.find-friends-sources ul.find-friends-list li.js-service-row div.js-service-name b{
	font-weight:normal !important;
}
/*アドレス帳を検索*/
div#stream-items-id.js-stream-items div.find-friends-sources ul.find-friends-list li.js-service-row a.btn
/*友だちを招待*/
,div#discover-stream-manager.js-stream-manager div.stream-container div.stream div.component div.component form.invite-friends table.flex-table tbody tr td.flex-table-secondary button.btn{
	font-weight:normal !important;
}
/*カテゴリ別に見る*/
div#stream-items-id.js-stream-items div.js-stream-item div.interest-category div.content div.stream-item-header a strong{
	font-weight:normal !important;
}

/*------------------------------他の人のページ-----------------------------------*/
div#page-container.wrapper div div.dashboard div.module div.profile-tweet-box div.tweet-box div.tweet-box-title h2{
	font-weight:normal !important;
	color:#4F4F4F!important;
}
/*ツイートエリア*/
div#page-container.wrapper div div.dashboard div.module div.profile-tweet-box div.tweet-box div.text-area div.text-area-editor textarea.twitter-anywhere-tweet-box-editor{
	color:#4F4F4F!important;
}

}
