/*
 * Author:            Will (war59312@gmail.com)
 * Description:       Hides all the ads on fileserve.com begging you to upgrade your standard user account to Premium.
*/

/* +++ changelog +++
April 16th, 2012
- fixed it
July 14th, 2011
- Hides "Hot Weekend Promotion" Ad
June 8th, 2011
- Cleans up 404 error landing page
March 29th, 2011
- Hides "Become a Premium user" On Deleted List
- Hides "Choose your FileServe Premium plan to get" On Deleted List
- Hides "Choose your plan" On Deleted List
- Hides "Download with Fileserve Manager"
- Hides "Login as a Premium user" On Error 404 Page
- Centers Error Messages On Deleted List
March 18th, 2011
- Hides "Choose your FileServe Premium plan" On Download Page (Only Appears On "File not available" Page)
March 3rd, 2011
- Hides "Make Money With VideoBB" Ad
Jan 26nd, 2011
- Hides "Become premium member to download all files" Link On List Page
Jan 22nd, 2011
- Hides "FileServe Premium" Ad On Download Page
- Hides "Advertisement" On Download Page
- Hides "iframe" Ads On Download Page
Dec 30th, 2010
- Hides "PopUp Upgrade to Premium" Ad
- Hides "Make Money" Tab In Header
- Hides "Upgrade Panel" On Dashboard
Dec 19th, 2010
- Hides Login Box Since This Is For Non Premium Members
- Hides "Upgrade to Premium" Button
- Centers "Download Count Down"
- Centers "File Information"
- Centers "Bookmark and Share"
Dec 3rd, 2010
- Now Cleans The Download Page Too
Nov 28th, 2010
- Original Release
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(fileserve.com) {

/* Makes Site Wide */
#container { width: 98% !important }

/* Hides "Make Money With VideoBB" & "hotimg.com" Ads */
a[class*="promo"] { display: none !important }

/* Hides "PopUp Upgrade to Premium" Ad */
.dialog_overlay { display: none !important }

/* Hides "Upgrade to Premium" in Header */
#menu_upgrade_premium { display: none !important }

/* Hides "Premium" Tab In Header */
#menu_premium { display: none !important }

/* Hides "Make Money" Tab In Header */
#menu_money, #menu_login_money { display: none !important }

/* Hides "Upgrade" and "Premium" Links */
a[href*="upgrade"], a[href*="premium"] { display: none !important }

/* Hides "upgrade to Premium account now." On The "Your download link has expired" Page */
a[onclick*="displayOverlayDialog('dialog_overlay');"] { display: none !important }

/* Hides "Benefits of signing up" On SignUp Page */
.right_box.comparison { display: none !important }

/* Hides "Tips" */
.yellow_tips { display: none !important }

/* Hides "Upgrade To Fileserve Premium" Baner Ads */
.banner, IMG[src*="banner"], .compare, .plan { display: none !important }

/* Hides "Upgrade Panel" On Dashboard */
.panel.upgrade { display: none !important }

/* Hides "AddThis" Text Ad */
.addthis_button { display: none !important }

/* Hides "Hot Weekend Promotion" Ad */
.summer_banner { display: none !important }

/* Hides Footer */
.tail, #footer { display: none !important; position: relative !important; }

/* muahah too easy - removes all the ads that use iframes */
iframe { display:none !important; }

/* Hides NoScript Place Holder Icons */
[class="__noscriptPlaceholder__ "], .__noscriptPlaceholder__.__noscriptObjectPatchMe__ { display: none !important; }

}

@-moz-document url-prefix(http://www.fileserve.com/file/), url-prefix(http://fileserve.com/file/) {

/* Hides "iframe" Ads On Download Page */
IFRAME { display: none !important }

/* Hides "FileServe Premium" Ad On Download Page */
.planbanner.planbanner_780 { display: none !important }

/* Hides "Advertisement" On Download Page */
.ad_float { display: none !important }

/* Hides "CountDown Banner Ad" */
#countdown_banner_45day { display: none !important }

/* Hides "Become a Premium user now, click here" On Download Page */
a[onclick="displayOverlayDialog('dialog_overlay');"][href="#"] { display: none !important }

/* Hides "Timing" Ad On Download Page */
.timing { display: none !important }

/* Hides "Plans" On Download Page */
.panel.comparison { display: none !important }

/* Hides "Premium Download Box" On Download Page */
.premium { display: none !important }

/* Hides "Free User Text" On Download Page */
H4 { display: none !important }

/* Hides "Free User Speed Graph" On Download Page */
.speed_graph.basic { display: none !important }

/* Hides "Upgrade to Premium" Button Under Login Area On Download Page (Only Appears While Having To Wait To Download Next File) */
.upgrade_to_premium_btn { display: none !important }

/* Hides "Upgrade to Premium account now" Text Link On Download Page (Only Appears While Having To Wait To Download Next File) */
#middle_panel > DIV > DIV:first-child > DIV > UL > LI:last-child { display: none !important }

/* Hides "Choose your FileServe Premium plan" On Download Page (Only Appears On "File not available" Page) */
.dialog_choose_plan { display: none !important }

/* Hides "Log-In Box" On The Download Page (After All This Is For Non Prmium Members) */
.login_box { display: none !important }

/* Centers "Download Count Down" On Download Page (Only Appears While Having To Wait To Download Next File) */
.panel.file_download.premium_error { width: 50% !important; margin: 0px auto !important; }

/* Centers "File Information" On Download Page */
.panel.file_download { width: 500px !important; margin: 0px auto !important; }

/* Centers "Bookmark and Share" On Download Page */
.panel.file_links { width: 50% !important; margin: 0px auto !important; }

/* Hides "Download with Fileserve Manager" */
H5, a[href*="fileservemanager.com"] { display: none !important }

}

@-moz-document url-prefix(http://www.fileserve.com/list/), url-prefix(http://fileserve.com/list/), url-prefix(http://www.fileserve.com/error-404.html), url-prefix(http://fileserve.com/error-404.html) {

/* Hides "Become a Premium user" On Deleted List */
.premium_error { display: none !important }

/* Hides "Choose your FileServe Premium plan to get" On Deleted List */
.dialog_choose_plan { display: none !important }

/* Hides "Choose your plan" On Deleted List */
.panel.comparison { display: none !important }

/* Hides "Login as a Premium user" On Error 404 Page */
.panel.download_boxes { display: none !important }

/* Centers Error Messages On Deleted List */
.panel.file_download { width: 500px !important; margin: 0px auto !important; }

}

@-moz-document url-prefix(http://www.fileserve.com/landing-error.php?error_code=404), url-prefix(http://fileserve.com/landing-error.php?error_code=404) {

/* Hides "Choose your plan" On 404 page */
.panel.comparison { display: none !important }

/* Hides "Download Time" On 404 page */
.panel.download_boxes { display: none !important }

}
