css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") 
{
#playnav-grid-panel div.scrollbox-body
{
  height: 680px !important;
}
div.playnav-player-container
{
  height: 534px !important;
}
#playnav-body 
{
  height: 744px !important; 
}
#playnav-player 
{ 
  height: 530px !important; 
  width: 944px !important 
}
#playnav-play-panel 
{
  top: 534px !important; 
  height: 211px !important; 
}
#playnav-play-content div.scrollbox-body 
{ 
  height: 110px !important; 
}
#playnav-play-favorites-scrollbox div.scrollbox-body,
#playnav-play-all-scrollbox div.scrollbox-body,
#playnav-play-playlists-scrollbox div.scrollbox-body 
{
  height: 200px !important; 
}
#playnav-play-uploads-scrollbox div.scrollbox-body 
{
  height: 120px !important; 
}
#playnav-play-content 
{ 
  height: 100px !important; 
}
#movie_player
{
  height:100% !important;
  position:relative !important;
  width:100% !important;
}
}
