css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://minatokobe.com/wp/) {

/* Non-display part */
#NnVD,
body > a,
body > span,
#search,
#menu,
#sidebar,
#footer-wrap,
.wp_followme_c2,
.tweet-this,
#disqus_thread,
.dsq-brlink
{ display: none !important; }

/* Display part */
body,
#page-wrap,
#header-wrap,
#header,
#logo,
#content,
.post,
.title,
.post-wrap,
.content,
.info,
.post-content
{
    width: auto !important;
    background: #ffffff !important;
    color: #000000 !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    margin: 0 0 0 !important;
}

#header-wrap,
#header,
#logo
{
    background: #000000 !important;
}

.title
{
    background: #aaaaaa !important;
}

}
