css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("64digits.com") {

.top_main_color_bar_off a {color: #737373!important;}
.top_main_color_bar_on a {color: #972626!important;}
.top_main_color_bar_on:active {background-color: #000000!important;}

.whitebg {z-index: 10!important; font-family: verdana!important; font-size: 10px!important; padding: 0px!important; width: 150px!important;}

.left_blog_cell_off {padding: 0px!important; background: #ffffff!important;}

.left_blog_cell_on {padding: 0px!important;}

.left_bg_image {background: #fff!important; padding: 0px!important;}

.left_menu_bg {background-image: url('http://64digits.com/users/Alert%20Games/title2_64.png')!important; background-position: -1px 0px!important; background-repeat: no-repeat!important; border: 1px solid #d4d4d4!important; border-top: 0px!important; padding: 0px!important;}

.left_login_box {background-color: transparent!important; height: 1px!important; font-family: verdana!important; font-size: 10px!important; padding: 0px!important; padding-bottom: 20px!important; margin: 0px!important; color: #CC0000!important; border: 0px solid black!important; vertical-align: top!important;}

.left_input {background: #FF0000!important; font-family: Verdana!important; font-size: 12px!important; color: #CC0000!important;}

.left_login_box .ms_font {font-family: verdana!important; font-size: 10px!important; color: #CC0000!important; padding: 0px!important; margin: 0px!important;}

/*.top_redbar {background: url('http://64digits.com/users/Alert%20Games/red_grad.gif')!important;}*/

/* Quote Box */
.quote_header {background-color: #779977!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-bottom: 1px dashed #FFFFFF!important; text-align: center!important; font-family: "Verdana"!important;}
.quote {background-color: #557755!important; border: 1px solid #FFFFFF!important; border-top: 0px solid #FFFFFF!important; font-style: italic!important; color: #FFFFFF!important; font-family: "Verdana"!important; font-size: 12px!important;}

/* Code Box */
.code_header {background-color: #8899AA!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-bottom: 1px dashed #FFFFFF!important; text-align: center!important;}
.code {background-color: #556677!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-top: 0px solid #FFFFFF!important;}

}
