css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tiipsi.com") {

BODY, TD {
font-family: monospace !important;
font-size : 10px !important;
color: lightgrey !important; 
background-color: black !important;
}

A {
text-decoration: none !important;
font-weight: 900;
color: white !important;
}
A:Hover {
text-decoration: none !important;
color: silver !important;
}

input {
color: lightgrey !important;
background-color: black !important;
border: thin solid lightgrey !important;
}
input:hover {
border: thin solid white !important;
}
input:focus {
color: white !important;
border: thin solid white !important;
}

.black {
font-size: 11px !important;
}

.silver {
font-size: 10px !important;
color: silver !important;
}

.statField {
	background: black !important;
	border: 1px solid black !important;
}

.str {
	color: white !important;
        font-weight: bold;
}

#topmenucell {
border: medium dashed silver !important; 
background-color: black !important;
color: white !important;
font-weight: bold;
}

#bottommenucell {
border-left: 1px solid white !important;
border-bottom: 1px solid white !important;
border-right: 1px solid white !important;
border-top: 1px solid white !important;
padding: 5px !important;
background-color: black !important
}

#footer {
padding: 0 !important;
font-size: 10px !important;
color: #000000 !important;
}

.chatname {
font-size: 9px !important;
}
.navhi {
	color: #00FF00 !important;
	text-decoration: underline !important;
}

#MyHomeMenu {
	font-size: 15px !important;
	color: #FFFFFF !important;
	border: 1px solid #AAA !important;
	}

#MyHomeMenu ul {
	display: inline !important;
	list-style: none !important;
	margin: 0 !important;
	padding: 0 !important;
	border: none !important;
	}
		
#MyHomeMenu li {
	display: inline !important;
	padding: 0px 15px !important;
	border-left: 1px solid #BBB !important;
	margin: 0 !important;
	}
	
#MyHomeMenu li.first {
	border-left: none !important;
	margin: 0 !important;
	}
	
#MyHomeMenu a {
	text-decoration: underline !important;
	color: #FFF !important;
	font-size: 15px !important;
	}

#MyHomeMenu li:hover, #menunav li.over{
	background-color: #777777 !important;
	color: white !important;
	font-weight: bold !important;
	}

#MyHomeMenu a:hover {
	background-color: #777777 !important;
	color: white !important;
	font-weight: bold !important;
	}

pre {
    font-family: monospace !important;
    font-size: 9pt !important;
    }

font.mono {
    font-size: 9pt !important;
    }

font.small {
    font-size: 7pt !important;
}

.form
{
	border: 1px solid #CC0000 !important;
	background-color: #000000 !important;
	color: white !important;
}

#success
{
	background: red !important;
	color: white !important;
	font-weight: bold !important;
	font-size: 11px !important;
	position: absolute !important;
	top: 0 !important;
	left: 0 !important;
	height: 15px !important;
	width: 150px !important;
}

.error
{
	font-size: 11px !important;
	color: red !important;
	font-weight: bold !important;
	padding: 5px 0px 5px 0px !important;
}

.effect
{
	color: red !important;
	font-weight: bold !important;
	text-decoration: underline !important;
}

#tutorial
{
	font-size: 130% !important;
}

#tutorial b
{
	font-size: 140% !important;
}

.formField { font-size: 11px !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; }
.formField:hover { font-size: 11px !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; }
.formField:focus { font-size: 11px !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; }
.formFieldPt9 { font-size: 11px !important; border:1px solid #CC0000 !important; background-color:#000000 !important; color:white !important; font-size: 9pt !important; }

.tableBackgroundColor  {background-color: #000000 !important;}
.tableBackgroundColor2 {background-color: #181818 !important;}

.white {color:white !important;}
}
