css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newyorker.com") {

#articletext p, .entry-content p {
font-size: 16px !important;
font-weight:bold !important;
font-family:  Avenir-Heavy, eurostile, arial !important;
line-height:160% !important;
color:#555 !important;
font-weight:bold;
}
#content {
background-color: #F4F1E9 !important;
}
h1, h2, h3, h4, #articleheads h1, #articleintro, #articleauthor, p.caption, #ps3_fs3_yrail, #relatedlinks, #keywords, #relatedlinks dt, #keywords dt{
font-family: Avenir-Heavy, eurostile, arial !important;
text-transform:none;
}
h3.header a{
font-weight:bold !important;
color:#E17F40 !important;
}
#articleheads h1 {
font-weight:bold !important;
}
#relatedlinks dt, #keywords dt{
font-weight:bold !important;
font-size:14px !important;
}
p.caption{
font-style:normal !important;
}
.rubric{
font-weight:bold !important;
font-size:16px !important;
}

}
