css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.com") {

.sb_msi.sb

DIV, LI, TABLE, UL, H2, INPUT, TD, TR, .Ck[name="mCC"]
{-moz-border-radius: 15px 5px  !important;}
.ToolbarItem 
{-moz-box-shadow:1px 1px 1em lightblue; -moz-border-radius: 15px 1px !important;}
#contentLeft
{margin-top: 4%!important;-moz-border-radius: 30px 5px !important;}
#contentLeft {left:-285px!important;z-index:1000!important;background-color:white!important;overflow:visible!important;position:absolute!important;width:255px!important;-moz-box-shadow:0 1px 2em lightblue;-webkit-box-shadow:0 1px 1.5em lightblue;opacity:0.90!important;border-right:20px solid lightblue!important;border-left:20px solid lightblue!important;float:left !important;
position:fixed !important;width:auto !important;


}
.ContentFolderList {top:62px!important;width:255px!important}
.EmailName  {padding-left:10px!important;font-weight:bold!important;color:#993300!important;border-bottom:1px solid #eee!important;cursor:default!important}
.EmailName:before {content:".";color:transparent!important;background:url(data:image/png;base64,) no-repeat 0 0!important;padding-left:20px!important}


.LeftNav li, .LeftNav li a{border:1px solid transparent!important}
.LeftNav li a {line-height:16px!important}
.LeftNav li {border-bottom:1px solid lightblue!important}

.LeftNav li a:hover {border:1px solid transparent!important}

#contentLeft:hover {left:-5px!important}


.Bottom div.LeftNavHeading.DisplayBlock {cursor:default}
.Bottom div.LeftNavHeading.DisplayBlock:before {content:".";background:url(data:image/png;base64,)no-repeat 0 0!important;padding-left:20px!important;color:transparent!important}




/* Ads and clutter */

[id^="RadAd"],
[id^="CustComm"],
.VirusImg,
.AntiVirus,
.AttachmentContainer .ScanLogo,
.Crm120Container,
.DivLine, 
.SideAds.TextAlignRight {display:none !important;}


/* All pages */

body  {font-size:75% !important}
*  {line-height:115% !important;}
#c_header .inCenter  {visibility:hidden !important}
#MainContent  {right:0 !important; margin-right:6px !important}
#Middle {left:0 !important; margin-left:6px !important}


/* Inbox */

.ContentLeft li a,
.ContentFolderList .DisplayBlock,
a.ManageLink  {padding: 0.1em 0.3em !important;}
a.ManageLink  {margin-top:1em !important;}

table.InboxTable td  {padding-top:0 !important; padding-bottom:0 !important;}
table.InboxTable td.Chk input,
table.InboxTable td.Ico img  {position:relative !important; top:2px !important}
table.InboxTable td.Ico div  {float:none !important; text-align:center !important}

.MessageSelection[noSel] {display:none !important}


/* Compose */

#contactPickerHint  {display:none !important;}


/* Hide the Quick Add panel to the right of message composition */

#IAPWrapper  {display:none !important;}
#ManagedContentWrapper  {right:0 !important;}


/* Contacts list */

.MainLayoutLeftBar>* {margin-left:6px !important}

.CLT_NameCol  {padding:0 0 0 20px !important}
.cxp_ic_img_m  {display:none !important}
.cxp_ic_text_h  {padding:0 0 0 0.8em !important}


.MsgListToolbarsContainer, .sb_msi.sb, #messageListSplitPane, #readingPaneContainer, DIV#toolbarContainer , #composeHeader, #composeMessage
{margin-left:-85px!important;-moz-box-shadow:0 1px 2em lightblue;}



.c_hta.t_hdbgimg, .c_hg, .t_hdbg 
{width: 99.8%!important;}
}
