css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gate.eveonline.com") {

/* User Style Sheet */

/* override inline styles from http://nataliejost.com/542 */
body * [style]
{
    color: inherit !important;
    font-size: inherit !important;
}
body
{
	font: 100% Frutiger, "Frutiger Linotype", Univers, Calibri, "Gill Sans", "Gill Sans MT", "Myriad Pro", Myriad, "DejaVu Sans Condensed", "Liberation Sans", "Nimbus Sans L", Tahoma, Geneva, "Helvetica Neue", Helvetica, Arial, sans-serif !important;
	/* from http://articles.sitepoint.com/print/eight-definitive-font-stacks */
	background: #fefefe !important;
	background-image: none !important;
}
.colmaskStretch, .colmaskConstrained
{
	background: #888 !important;
}
input.textfield, textarea.textfield, select.textfield, p
{
	font-size: 12px !important;
}
a.vertNav, a.vertContainer, .vertContainer, a.vertNavUpdates
{
	background: #555 !important;
}
.vertNavActive, .vertNavActiveUpdates, .vertNav.current, .vertNav.current span
{
	background: #444 !important;
}
.leftmenu, .fullpage .colleft, .rightmenu .colleft, .colmid
{
	background: #bbb !important;
}
.leftmenu .colleft, .rightmenu, .colmid .colright
{
	background: #888 !important;
	color: #333 !important;
}
#sectionHeaderContainer, #timeDateContainer
{
	color: #777 !important;
}
#mailTable.tablesorter tbody td, #contactsOverview, #broadcastContainer, #broadcastContainerForm, .col2, .tabs-1-panel
{
	color: #aaa !important;
}
.commentContainer
{
	border-bottom: 1px solid #555 !important;
	background: #888 !important;
}
.statusTime, .statusTimeUpcomingEvents
{
	color: #444 !important;
}
#footerContainer
{
	background-color: #fefefe !important;
}
img[alt="PEGI INFO"], img[alt="PEGI RATING"], img[alt="ESRB Teen"]
{
	display: none !important;
}
span.grayText, span.grayTextBold
{
	color: #999 !important;
	font-size: 11px !important;
}

/*
table.tablesorter tbody td {
	background-color: #292929;
	background: #555 !important;
}
table.tablesorter tbody tr.odd td {
	background-color: #313131;
	background: #777 !important;
}
*/

}
